}

void delete()
{
    JmgStampJournalTrans    deletedTrans;
    if (jmgStampJournalTrans.QtyError || jmgStampJournalTrans.QtyGood || jmgStampJournalTrans.QtyStartup)
    {
        throw error("@SYS38761");
    }

    deletedTrans.data(jmgStampJournalTrans);

    super();

    jmgCalcApproveForm.transactionDeleted(deletedTrans);

    if (jmgStampJournalTrans.StampType == JmgStampTypeEnum::Absence)
        jmgStampJournalTrans_Absence_DS.executeQuery();
}

int active()
{
    int     ret;

    ret = super();

    jmgStampJournalTrans_ds.object(fieldNum(JmgStampJournalTrans,JobId)).allowEdit(jmgStampJournalTrans.StampType != JmgStampTypeEnum::Absence);
    jmgStampJournalTrans_ds.object(fieldNum(JmgStampJournalTrans,JobRef)).allowEdit(jmgStampJournalTrans.StampType != JmgStampTypeEnum::Absence);
    jmgStampJournalTrans_ds.object(fieldNum(JmgStampJournalTrans,StopTime)).allowEdit(!(jmgStampJournalTrans.JourRegType == JmgJourRegTypeEnum::SignIn || jmgStampJournalTrans.JourRegType == JmgJourRegTypeEnum::SignOut));

    jmgProdQuantityChange.enabled(jmgStampJournalTrans.JourRegType  == JmgJourRegTypeEnum::MrpProcess   &&
                                  jmgStampJournalTable.Calculated   == NoYes::No);

    ViewErrorCauseButton.enabled(jmgStampJournalTrans.QtyError || jmgStampJournalTrans.PdsCWQtyError);

    return ret;
}

public void lookup(FormControl _formControl, str _filterStr)
{
    ProjTableLookup projTableLookup;
    SysTableLookup  sysTableLookup;

    if (jmgStampJournalTrans.JourRegType    == JmgJourRegTypeEnum::PrjActivity  ||
        jmgStampJournalTrans.JourRegType    == JmgJourRegTypeEnum::PrjProject)
    {
        sysTableLookup  = SysTableLookup::newParameters(tableNum(ProjTable), _formControl);
        projTableLookup = new ProjTableLookup();
        sysTableLookup.addLookupfield(fieldNum(ProjTable, ProjId), true);
        sysTableLookup.addLookupfield(fieldNum(ProjTable, Name), false);
        sysTableLookup.parmQuery(projTableLookup.buildProjTableQueryResource(jmgStampJournalTrans.TableId, jmgStampJournalTrans.ProjCategory,
            ResourceFacade::findOrCreateByWorker(jmgStampJournalTrans.Worker)));
        sysTableLookup.performFormLookup();
    }
    else
    {
        super(_formControl, _filterStr);
    }
}

