public void executeQuery()
{
    if (mode == JmgCalcApproveMode::Calculate)
    {
        rangeCalcGroupWorker.enabled(true);
        rangeCalcGroupWorker.value(jmgGroupCalcId);
    }
    else
    {
        rangeCalcGroupWorker.enabled(false);
    }

    if (mode == JmgCalcApproveMode::Approve)
    {
        rangeApprGroupWorker.enabled(true);
        rangeApprGroupWorker.value(jmgGroupApproveId);
    }
    else
    {
        rangeApprGroupWorker.enabled(false);
    }

    super();
}

public int active()
{
    int ret;
    date filterDate;
    ret = super();

    rangeWorker.enabled(true);
    rangeWorker.value(queryValue(JmgEmployee.Worker));
    filterDate = max(JmgStampJournalTable.ProfileDate, JmgEmployee.ActiveFrom);
    JmgStampJournalTable_DS.executeQuery();
    JmgStampJournalTable_DS.positionToRecordByValue(fieldNum(JmgStampJournalTable, ProfileDate), queryValue(filterDate));

    return ret;
}

