void clicked()
{
    if (! jmgStampJournalTable.RecId)
    {
        jmgStampJournalTable.ProfileDate        = viewDate;
        if (mode != JmgCalcApproveMode::Approve)
        {
            jmgStampJournalTable.CalculateGroupId   = jmgGroupCalcId;
        }
        else
        {
            jmgStampJournalTable.ApproveGroupId     = jmgGroupApproveId;
        }
    }

    super();

    if (filterError == NoYes::Yes)
    {
        element.switchFilter();
    }
}

void clicked()
{
    if (! jmgStampJournalTable.RecId)
    {
        jmgStampJournalTable.ProfileDate        = viewDate;
        jmgStampJournalTable.ApproveGroupId     = jmgGroupApproveId;
    }

    super();

    if (filterError == NoYes::Yes)
    {
        element.switchFilter();
    }
}

void clicked()
{
    if (! jmgStampJournalTable.RecId)
    {
        jmgStampJournalTable.ProfileDate        = viewDate;
        jmgStampJournalTable.ApproveGroupId     = jmgGroupApproveId;
    }

    super();

    if (filterError == NoYes::Yes)
        element.switchFilter();
}

void clicked()
{
    Args            args;

    args = new Args();
    args.record(jmgStampJournalTable);
    args.parmEnumType(enumNum(NoYes));
    args.parmEnum(mode == JmgCalcApproveMode::Approve ? NoYes::Yes : NoYes::No);

    new MenuFunction(menuitemOutputStr(JmgEmployeeTimeSpec), MenuItemType::Output).run(args);
}

void clicked()
{
    element.switchFilter();

    super();
}

public boolean modified()
{
    boolean ret;

    ret = super();

    JmgGroupCalcId newGroupCalcId = this.valueStr();

    if (newGroupCalcId)
    {
        jmgGroupCalcId  = newGroupCalcId;

        rangeGroupCalcId.value(queryValue(jmgGroupCalcId));
        JmgStampJournalTable::makeLines(viewDate, jmgGroupCalcId, '');
        jmgStampJournalTable_ds.executeQuery();

        xSysLastValue::saveLast(element);
    }

    this.text(jmgGroupCalcId);

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    JmgGroupCalcId newGroupApproveId = this.valueStr();

    if (newGroupApproveId)
    {
        jmgGroupApproveId  = newGroupApproveId;

        rangeGroupApproveId.value(queryValue(jmgGroupApproveId));
        JmgStampJournalTable::makeLines(viewDate,(mode == JmgCalcApproveMode::Approve ? '' : jmgGroupCalcId),(mode == JmgCalcApproveMode::Approve ? jmgGroupApproveId : ''));
        jmgStampJournalTable_ds.executeQuery();

        xSysLastValue::saveLast(element);
    }

    this.text(jmgGroupApproveId);

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    element.initializeData(this.dateValue());

    return ret;
}

