Method.Source:
[Form]
public class JmgJobIdLookUp extends FormRun
{
    #define.ActivityPage(1)
    #define.ProdJobPage(2)
    #define.ProjPage(3)
    #define.ProjActivityPage(4)
    #define.ProjBudgetPage(5)
    #define.AbsenceGroupPage(7)
    #define.AbsenceCodePage(6)

    FormStringControl   callerControl;
    RouteJobType        prodRouteJobType;
    Map                 tab2ComboBoxItemMap;
    private JmgJobId    jmgJobId;
}

public void init()
{
    if (! element.args().caller())
    {
        element.form().design().frame(FormFrame::Standard);
    }

    super();
}

FormControl pageSelectCtrl(int tabNo)
{
    switch (tabNo)
    {
        case #ActivityPage:
            return jmgJobTable_IPCActivity_JobId;
        case #ProdJobPage:
            return jmgJobTable_Production_JobId;
        case #ProjPage:
            return jmgJobTable_Project_JobId;
        case #ProjActivityPage:
            return jmgJobTable_ProjActivity_JobId;
        case #ProjBudgetPage:
            return jmgJobTable_ProjBudget_JobId;
        case #AbsenceGroupPage:
            return jmgJobTable_AbsenceCodeGroup_JobId;
        case #AbsenceCodePage:
            return jmgJobTable_AbsenceCode_JobId;
    }
    return jmgJobTable_IPCActivity_JobId;
}

void run()
{
    boolean filterLookup    = false;

    if (element.args().caller())
    {
        callerControl   = SysTableLookup::getCallerStringControl(element.args());
    }

    if (callerControl && callerControl.text() && callerControl.hasChanged())
    {
        jmgJobId     = callerControl.text();
        filterLookup = true;
    }

    this.setupRanges();

    super();

    // Generate view combobox based on tabs
    tab2ComboBoxItemMap = SysLookup::tab2ComboBox(Tab, switchView);
}

void setSelectMode(int tabNo)
{
    element.selectMode(element.pageSelectCtrl(tabno));
}

private void setupAbsenceCodeGroupPage(JmgJourRegType _jourRegType, JmgModuleRefId _modulerefId)
{
    QueryBuildRange qbr;

    qbr = jmgJobTable_AbsenceCodeGroup_DS.query().dataSourceTable(tablenum(JmgJobTable)).addRange(fieldnum(JmgJobTable, Type));
    qbr.value(queryValue(JmgJobTableType::HRMAbsenceCodeGroup));

    if (_modulerefId)
    {
        jmgJobTable_AbsenceCodeGroup_DS.query().dataSourceTable(tablenum(JmgJobTable)).addRange(fieldnum(JmgJobTable, ModuleRefId)).value(_modulerefId);
    }
}

private void setupAbsenceCodePage(JmgJourRegType _jourRegType, JmgModuleRefId _modulerefId)
{
    QueryBuildRange qbr;

    qbr = jmgJobTable_AbsenceCode_DS.query().dataSourceTable(tablenum(JmgJobTable)).addRange(fieldnum(JmgJobTable, Type));
    qbr.value(queryValue(JmgJobTableType::HRMAbsenceCode));

    if (_modulerefId)
    {
        jmgJobTable_AbsenceCode_DS.query().dataSourceTable(tablenum(JmgJobTable)).addRange(fieldnum(JmgJobTable, ModuleRefId)).value(_modulerefId);
    }
}

private void setupActivityPage(JmgJourRegType _jourRegType, JmgModuleRefId _modulerefId, boolean _excludeSwitchCode = false)
{
    QueryBuildRange      qbr;
    Range                r;
    QueryBuildDataSource qbds = jmgJobTable_IPCActivity_DS.query().dataSourceTable(tablenum(JmgJobTable));

    qbr = qbds.addRange(fieldnum(JmgJobTable, Type));

    switch (_jourRegType)
    {
        case JmgJourRegTypeEnum::IndActivity:
            qbr.value(queryValue(JmgJobTableType::SFCActivity));
            break;
        case JmgJourRegTypeEnum::SignIn:
            r = queryValue(JmgJobTableType::SFCClockIn);
            r = queryRangeConcat(r, JmgJobTableType::SFCOnCall);
            qbr.value(r);
            break;
        case JmgJourRegTypeEnum::SignOut:
            qbr.value(queryValue(JmgJobTableType::SFCClockOut));
            break;
        case JmgJourRegTypeEnum::StartTeam:
            qbr.value(queryValue(JmgJobTableType::SFCStartTeam));
            break;
        case JmgJourRegTypeEnum::StopTeam:
            qbr.value(queryValue(JmgJobTableType::SFCStopTeam));
            break;
        case JmgJourRegTypeEnum::BreakReg:
            qbr.value(queryValue(JmgJobTableType::SFCBreak));
            break;
        default:
            r = queryValue(JmgJobTableType::SFCActivity);
            r = queryRangeConcat(r, JmgJobTableType::SFCBreak);
            r = queryRangeConcat(r, JmgJobTableType::SFCClockIn);
            r = queryRangeConcat(r, JmgJobTableType::SFCClockOut);
            if (!_excludeSwitchCode)
            {
                r = queryRangeConcat(r, JmgJobTableType::SFCEventCode);
            }
            r = queryRangeConcat(r, JmgJobTableType::SFCOnCall);
            r = queryRangeConcat(r, JmgJobTableType::SFCStartTeam);
            r = queryRangeConcat(r, JmgJobTableType::SFCStopTeam);
            qbr.value(r);
            break;
    }

    if (_modulerefId)
    {
        qbds.addRange(fieldnum(JmgJobTable, ModuleRefId)).value(_modulerefId);
    }

    if (jmgJobId)
    {
        qbds.addRange(fieldnum(JmgJobTable, JobId)).value(jmgJobId);
    }
}

private void setupProductionPage(
    JmgJourRegType  _jourRegType,
    JmgModuleRefId  _modulerefId,
    InventSiteId    _inventSiteId,
    ProdUnitId      _prodUnitId,
    WrkCtrId        _wrkCtrId)
{
    QueryBuildRange qbr;
    WrkCtrTable     selectedWrkCtrTable;
    Range           r;

    qbr = jmgJobTable_Production_DS.query().dataSourceTable(tablenum(JmgJobTable)).addRange(fieldnum(JmgJobTable, Type));

    switch (_jourRegType)
    {
        case JmgJourRegTypeEnum::MrpOverlap:
            qbr.value(queryValue(JmgJobTableType::ProdOverlap));
            break;
        case JmgJourRegTypeEnum::MrpProcess:
            qbr.value(queryValue(JmgJobTableType::ProdProcess));
            break;
        case JmgJourRegTypeEnum::MrpQueueAfter:
            qbr.value(queryValue(JmgJobTableType::ProdQueueAfter));
            break;
        case JmgJourRegTypeEnum::MrpQueueBefore:
            qbr.value(queryValue(JmgJobTableType::ProdQueueBefore));
            break;
        case JmgJourRegTypeEnum::MrpSetup:
            qbr.value(queryValue(JmgJobTableType::ProdSetup));
            break;
        case JmgJourRegTypeEnum::MrpTransport:
            qbr.value(queryValue(JmgJobTableType::ProdTransport));
            break;
        default:
            r = queryValue(JmgJobTableType::ProdOverlap);
            r = queryRangeConcat(r, JmgJobTableType::ProdProcess);
            r = queryRangeConcat(r, JmgJobTableType::ProdQueueAfter);
            r = queryRangeConcat(r, JmgJobTableType::ProdQueueBefore);
            r = queryRangeConcat(r, JmgJobTableType::ProdSetup);
            r = queryRangeConcat(r, JmgJobTableType::ProdTransport);
            qbr.value(r);
            break;
    }

    if (_modulerefId)
    {
        jmgJobTable_Production_DS.query().dataSourceTable(tablenum(JmgJobTable)).addRange(fieldnum(JmgJobTable, ModuleRefId)).value(_modulerefId);
    }

    if (_inventSiteId)
    {
        jmgJobTable_Production_DS.query().dataSourceTable(tablenum(JmgJobTable)).addRange(fieldnum(JmgJobTable, InventSiteId)).value(_inventSiteId);
    }

    if (_prodUnitId)
    {
        WrkCtrResourceAndGroupView_Production_DS.query()
            .dataSourceTable(tablenum(WrkCtrResourceAndGroupView))
            .addRange(fieldnum(WrkCtrResourceAndGroupView, ProdUnitId))
            .value(_prodUnitId);
    }

    if (_wrkCtrId)
    {
        selectedWrkCtrTable = WrkCtrTable::find(_wrkCtrId);

        if (!selectedWrkCtrTable.IsIndividualResource)
        {
            jmgJobTable_Production_DS.query().dataSourceTable(tablenum(JmgJobTable)).addRange(fieldnum(JmgJobTable, ResourceGroup)).value(queryValue(WrkCtrResourceGroup::findWrkCtrId(_wrkCtrId).RecId));
        }
        else
        {
            jmgJobTable_Production_DS.query().dataSourceTable(tablenum(JmgJobTable)).addRange(fieldnum(JmgJobTable, WrkCtrId)).value(_wrkCtrId);
        }
    }
}

private void setupProjectActivityPage(
    JmgJourRegType  _jourRegType,
    JmgModuleRefId  _modulerefId,
    JmgWorkerRecId  _worker,
    InventSiteId    _inventSiteId,
    ProdUnitId      _prodUnitId,
    WrkCtrId        _wrkCtrId)
{
    QueryBuildRange         qbrInventSiteId;

    QueryBuildDataSource qbdsJmgJobTable = jmgJobTable_ProjActivity_DS.query().dataSourceTable(tablenum(JmgJobTable));
    QueryBuildRange qbr = qbdsJmgJobTable.addRange(fieldnum(JmgJobTable, Type));
    qbr.value(queryValue(JmgJobTableType::ProjActivity));

    if (_modulerefId)
    {
        jmgJobTable_ProjActivity_DS.query().dataSourceTable(tablenum(JmgJobTable)).addRange(fieldnum(JmgJobTable, ModuleRefId)).value(_modulerefId);
    }

    qbdsJmgJobTable.addRange(fieldnum(JmgJobTable,ReportedFinished)).value(queryValue(NoYes::No));
    qbdsJmgJobTable.addSortField(fieldnum(JmgJobTable, FromDate), SortOrder::Ascending);
    qbdsJmgJobTable.addSortField(fieldnum(JmgJobTable, FromTime), SortOrder::Ascending);

    if (_inventSiteId)
    {
        qbrInventSiteId = qbdsJmgJobTable.addRange(fieldnum(JmgJobTable, InventSiteId));
        qbrInventSiteId.value(_inventSiteId);
    }

    if (_worker && _jourRegType == JmgJourRegTypeEnum::PrjActivity && ProjParameters::find().ValidationEmplProj != ProjValParameter::None)
    {
        ResourceRecId resource = ResourceFacade::findOrCreateByWorker(_worker);

        if (ResourceFacade::validateProjectResource(resource))
        {
            QueryBuildDataSource qbdsProjValEmplProjSetup = qbdsJmgJobTable.addDataSource(tablenum(ProjValEmplProjSetup));
            qbdsProjValEmplProjSetup.addLink(fieldnum(JmgJobTable, ModuleRefId), fieldnum(ProjValEmplProjSetup, Projid));
            qbdsProjValEmplProjSetup.joinMode(JoinMode::ExistsJoin);

            QueryBuildDataSource qbdsProjValEmplProjSetupGroup = qbdsProjValEmplProjSetup.addDataSource(tablenum(ProjValEmplProjSetup));
            qbdsProjValEmplProjSetupGroup.addLink(fieldnum(ProjValEmplProjSetup, GroupId), fieldnum(ProjValEmplProjSetup, GroupId));
            qbdsProjValEmplProjSetupGroup.joinMode(JoinMode::ExistsJoin);
            qbdsProjValEmplProjSetupGroup.addRange(fieldnum(ProjValEmplProjSetup, Resource)).value(queryValue(resource));

            if (qbrInventSiteId)
            {
                qbrInventSiteId.value(queryRangeConcat(qbrInventSiteId.value(), ""));
            }

            qbdsJmgJobTable.addRange(fieldnum(JmgJobTable, Worker)).value(queryRangeConcat(queryValue(_worker), 0));
        }
    }
}

private void setupProjectBudgetPage(
    JmgJourRegType  _jourRegType,
    JmgModuleRefId  _modulerefId,
    JmgWorkerRecId  _worker,
    InventSiteId    _inventSiteId,
    ProdUnitId      _prodUnitId,
    WrkCtrId        _wrkCtrId)

{
    QueryBuildRange         qbrInventSiteId;

    QueryBuildDataSource qbdsJmgJobTable = jmgJobTable_ProjBudget_DS.query().dataSourceTable(tablenum(JmgJobTable));
    QueryBuildRange qbr = qbdsJmgJobTable.addRange(fieldnum(JmgJobTable, Type));
    qbr.value(queryValue(JmgJobTableType::ProjBudget));

    if (_modulerefId)
    {
        jmgJobTable_ProjBudget_DS.query().dataSourceTable(tablenum(JmgJobTable)).addRange(fieldnum(JmgJobTable, ModuleRefId)).value(_modulerefId);
    }

    qbdsJmgJobTable.addRange(fieldnum(JmgJobTable,ReportedFinished)).value(queryValue(NoYes::No));
    qbdsJmgJobTable.addSortField(fieldnum(JmgJobTable, FromDate), SortOrder::Ascending);
    qbdsJmgJobTable.addSortField(fieldnum(JmgJobTable, FromTime), SortOrder::Ascending);

    if (_inventSiteId)
    {
        qbrInventSiteId = qbdsJmgJobTable.addRange(fieldnum(JmgJobTable, InventSiteId));
        qbrInventSiteId.value(_inventSiteId);
    }

    if (_worker && (_jourRegType == JmgJourRegTypeEnum::PrjActivity || _jourRegType == JmgJourRegTypeEnum::PrjProject))
    {
        ResourceRecId resource = ResourceFacade::findOrCreateByWorker(_worker);

        if (ProjParameters::find().ValidationEmplProj != ProjValParameter::None && ResourceFacade::validateProjectResource(resource))
        {
            QueryBuildDataSource qbdsProjValEmplProjSetup = qbdsJmgJobTable.addDataSource(tablenum(ProjValEmplProjSetup));
            qbdsProjValEmplProjSetup.addLink(fieldnum(JmgJobTable, ModuleRefId), fieldnum(ProjValEmplProjSetup, Projid));
            qbdsProjValEmplProjSetup.joinMode(JoinMode::ExistsJoin);

            QueryBuildDataSource qbdsProjValEmplProjSetupGroup = qbdsProjValEmplProjSetup.addDataSource(tablenum(ProjValEmplProjSetup));
            qbdsProjValEmplProjSetupGroup.addLink(fieldnum(ProjValEmplProjSetup, GroupId), fieldnum(ProjValEmplProjSetup, GroupId));
            qbdsProjValEmplProjSetupGroup.joinMode(JoinMode::ExistsJoin);
            qbdsProjValEmplProjSetupGroup.addRange(fieldnum(ProjValEmplProjSetup, Resource)).value(queryValue(resource));

            if (qbrInventSiteId)
            {
                qbrInventSiteId.value(queryRangeConcat(qbrInventSiteId.value(), ""));
            }

            qbdsJmgJobTable.addRange(fieldnum(JmgJobTable, Worker)).value(queryRangeConcat(queryValue(_worker), 0));
        }
    }
}

