void setupRanges()
{
    JmgJourRegType      jourRegType;
    JmgModuleRefId      moduleRefId;
    JmgWorkerRecId      worker;
    WrkCtrId            wrkCtrId;
    ProdUnitId          prodUnitId;
    InventSiteId        inventSiteId;

    JmgStampTransMap    jmgStampTransMap;
    JmgTimecardTrans    jmgTimecardTrans;
    FormRun             formRun;
    Object              registrationForm;
    JmgRegistrationSetup jmgRegistrationSetup;
    JmgPieceRateLine    jmgPieceRateLine;

    boolean             excludeSwitchCode = false;

    switch (this.args().dataset())
    {
        case tablenum(JmgStampJournalTrans) :
        case tablenum(JmgStampTrans)        :
        case tablenum(JmgStampTransMap)     :
            jmgStampTransMap.data(this.args().record());
            jourRegType         = jmgStampTransMap.JourRegType;
            moduleRefId         = jmgStampTransMap.JobRef;
            worker              = jmgStampTransMap.Worker;
            excludeSwitchCode   = true;
            break;
        case tablenum(JmgTimecardTrans):
            jmgTimecardTrans    = this.args().record() as JmgTimecardTrans;
            jourRegType         = jmgTimecardTrans.JourRegType;
            moduleRefId         = jmgTimecardTrans.JobRef;
            worker              = jmgTimecardTrans.Worker;
            excludeSwitchCode   = true;
            break;
        case tablenum(JmgPieceRateLine):
            jmgPieceRateLine    = this.args().record() as JmgPieceRateLine;
            moduleRefId         = jmgPieceRateLine.ProdId;
            break;
        default:
            formRun = this.args().caller();
            if (formRun && formRun.name() == formstr(JmgRegistration))
            {
                registrationForm    = formRun;
                worker              = registrationForm.curWorker();
                jmgRegistrationSetup= registrationForm.getRegistrationSetup();
                if (jmgRegistrationSetup.WrkCtrValidation == NoYes::Yes)
                {
                    inventSiteId        = registrationForm.emplInventSiteId();
                    prodUnitId          = registrationForm.emplProdUnitId();
                    wrkCtrId            = registrationForm.emplWrkCtrId();
                }
            }
            break;
    }

    this.setupActivityPage(jourRegType, moduleRefId, excludeSwitchCode);
    this.setupProductionPage(jourRegType, moduleRefId, inventSiteId, prodUnitId, wrkCtrId);
    this.setupProjectPage(jourRegType, moduleRefId, worker, inventSiteId, prodUnitId, wrkCtrId);
    this.setupProjectActivityPage(jourRegType, moduleRefId, worker, inventSiteId, prodUnitId, wrkCtrId);
    this.setupProjectBudgetPage(jourRegType, moduleRefId, worker, inventSiteId, prodUnitId, wrkCtrId);
    this.setupAbsenceCodeGroupPage(jourRegType, moduleRefId);
    this.setupAbsenceCodePage(jourRegType, moduleRefId);

    if (this.args().dataset() == tablenum(JmgPieceRateLine))
    {
        jourRegType = JmgJourRegTypeEnum::MrpProcess;
    }

    this.setVisiblePages(jourRegType);

    formRun = this.args().caller();
    if (formRun && formRun.name() == formstr(JmgRegistration))
    {
        // Do not include reported as finished jobs on lookup.
        jmgJobTable_Production_DS.query().dataSourceTable(tablenum(JmgJobTable)).addRange(fieldnum(JmgJobTable, ReportedFinished)).value(queryValue(NoYes::No));
    }
}

void setVisiblePages(JmgJourRegType _jourRegType)
{
    activityPage.visible(false);
    projectPage.visible(false);
    prodJobPage.visible(false);
    projectActivityPage.visible(false);
    projectBudgetPage.visible(false);
    hrmAbsenceCodeGroupPage.visible(false);
    hrmAbsenceCodePage.visible(false);
    tab.showTabs(false);

    switch (_jourRegType)
    {
        case JmgJourRegTypeEnum::IndActivity:
        case JmgJourRegTypeEnum::SignIn:
        case JmgJourRegTypeEnum::SignOut:
        case JmgJourRegTypeEnum::StartTeam:
        case JmgJourRegTypeEnum::StopTeam:
        case JmgJourRegTypeEnum::BreakReg:
            activityPage.visible(true);
            this.setSelectMode(#ActivityPage);
            break;
        case JmgJourRegTypeEnum::MrpOverlap:
        case JmgJourRegTypeEnum::MrpProcess:
        case JmgJourRegTypeEnum::MrpQueueAfter:
        case JmgJourRegTypeEnum::MrpQueueBefore:
        case JmgJourRegTypeEnum::MrpSetup:
        case JmgJourRegTypeEnum::MrpTransport:
            prodJobPage.visible(true);
            this.setSelectMode(#ProdJobPage);
            break;
        case JmgJourRegTypeEnum::PrjProject:
            projectPage.visible(true);
            projectBudgetPage.visible(true);
            this.setSelectMode(#ProjPage);
            break;
        case JmgJourRegTypeEnum::PrjActivity:
            projectActivityPage.visible(true);
            projectBudgetPage.visible(true);
            this.setSelectMode(#ProjActivityPage);
            break;
        case JmgJourRegTypeEnum::Absence:
            hrmAbsenceCodeGroupPage.visible(true);
            hrmAbsenceCodePage.visible(true);
            this.setSelectMode(#AbsenceCodePage);
            break;
        default:
            activityPage.visible(true);
            projectPage.visible(true);
            prodJobPage.visible(true);
            projectActivityPage.visible(true);
            projectBudgetPage.visible(true);
            hrmAbsenceCodeGroupPage.visible(true);
            hrmAbsenceCodePage.visible(true);
            this.setSelectMode(#ActivityPage);
            break;
    }
}

