public boolean modified()
{
    boolean ret;

    ret = super();

    Tab.tabChanged(Tab.tabValue(), tab2ComboBoxItemMap.lookup(this.selection()));

    return ret;
}

void tabChanged(int fromTab, int toTab)
{
    super(fromTab, toTab);

    element.setSelectMode(totab);
}
