Method.Source:
    [Form]
    public class JmgJobRefLookUp extends FormRun
    {
    #define.CatePage(1)
    #define.ProdPage(2)
    #define.ProjPage(3)
    #define.AbsencePage(4)

        FormStringControl   callerControl;
        FormStringControl   filterControl;

}

container analyzeTmpQuery(TmpSysQuery tmpquery)
{
#Define.UsrFldIDAdd(65536)

    tableId                 inTableId       = tmpquery.Table_Id;
    JmgJourRegTypeEnum      jourRegType;
    JmgJobRef               jobRef          = '';
    JmgStampTypeEnum        stamptype;
    JmgModuleEnum           module;
    JmgIpcCategory          ipcCategory;
    ProjTable               projTab;
    ProdTable               prodTab;
    Range                   wrkRange;

    Integer                 fldidModule;
    Integer                 fldidJobRef;
    Integer                 fldidJourReg;
    Integer                 fldidStampType;

    boolean                 limitModule     = false;
    boolean                 limitJobRef     = false;
    boolean                 limitJourReg    = false;
    boolean                 limitStampType  = false;
    boolean                 limitType       = false;

    switch (inTableId)
    {
        case tablenum(JmgStampJournalTrans) :
            fldidModule     = fieldnum(JmgStampJournalTrans,Module);
            fldidJobRef     = fieldnum(JmgStampJournalTrans,JobRef);
            fldidJourReg    = fieldnum(JmgStampJournalTrans,JourRegType);
            fldidStampType  = fieldnum(JmgStampJournalTrans,StampType);
            break;
        case tablenum(JmgStampTrans) :
            fldidModule     = fieldnum(JmgStampTrans,Module);
            fldidJobRef     = fieldnum(JmgStampTrans,JobRef);
            fldidJourReg    = fieldnum(JmgStampTrans,JourRegType);
            fldidStampType  = fieldnum(JmgStampTrans,StampType);
            break;
        case tablenum(JmgIpcActivity) :
            fldidJobRef     = fieldnum(JmgIpcActivity,Category);
            fldidStampType  = fieldnum(JmgIpcActivity,Type);
            limitModule     = true;
            module          = JmgModuleEnum::IPC;
            break;
        case tablenum(JmgIpcCategory) :
            fldidJobRef     = fieldnum(JmgIpcCategory,Category);
            fldidStampType  = fieldnum(JmgIpcCategory,Type);
            limitModule     = true;
            module          = JmgModuleEnum::IPC;
            break;
    }
    tmpquery.setTmpData(this.args().record());

    while select tmpquery
          where (tmpquery.Table_Id == inTableId) &&
                (tmpquery.RangeValue)
    {
        wrkRange = tmpquery.RangeValue;

        if ((fldidModule) && ((tmpquery.Field_Id mod #UsrFldIDAdd) == fldidModule) && (!limitModule))
        {
            limitModule     = true;
            module          = str2enum(module,wrkRange);
        }
        else if ((fldidJobRef) && ((tmpquery.Field_Id  mod #UsrFldIDAdd) == fldidJobRef) && (!limitJobRef))
        {
            limitJobRef     = true;
            jobRef          = wrkRange;
        }
        else if ((fldidJourReg) && ((tmpquery.Field_Id  mod #UsrFldIDAdd) == fldidJourReg) && (!limitJourReg))
        {
            limitJourReg    = true;
            jourRegType     = str2enum(jourRegType,wrkRange);
        }
        else if ((fldidStampType) && ((tmpquery.Field_Id  mod #UsrFldIDAdd) == fldidStampType) && (!limitStampType))
        {
            limitStampType  = true;
            stamptype       = str2enum(stamptype,wrkRange);
        }
    }

    if (! limitJourReg)
    {
        if (limitModule)
        {
            switch (module)
            {
                case JmgModuleEnum::IPC :
                    switch (stamptype)
                    {
                        case JmgStampTypeEnum::Work :
                            jourRegType = JmgJourRegTypeEnum::IndActivity;
                            break;
                        case JmgStampTypeEnum::Absence :
                            jourRegType = JmgJourRegTypeEnum::Absence;
                            break;
                        case JmgStampTypeEnum::Break :
                            jourRegType = JmgJourRegTypeEnum::BreakReg;
                            break;
                    }
                    limitJourReg        = true;
                    limitType           = true;
                    break;
                case JmgModuleEnum::PROD :
                    jourRegType = JmgJourRegTypeEnum::MrpQueueBefore;
                    break;

                case JmgModuleEnum::PROJ :
                    jourRegType = JmgJourRegTypeEnum::PrjProject;
                    break;
            }
        }
        else
        {
            if (limitJobRef)
            {
                select firstonly Type,RecId from ipcCategory where ipcCategory.Category == jobRef;
                if (ipcCategory)
                {
                    switch (ipcCategory.Type)
                    {
                        case JmgStampTypeEnum::Work :
                            jourRegType     = JmgJourRegTypeEnum::IndActivity;
                            limitJourReg    = true;
                            break;
                        case JmgStampTypeEnum::Absence :
                            jourRegType     = JmgJourRegTypeEnum::Absence;
                            limitJourReg    = true;
                            break;
                        case JmgStampTypeEnum::Break :
                            jourRegType     = JmgJourRegTypeEnum::BreakReg;
                            limitJourReg    = true;
                            break;
                    }
                }
                else
                {
                    select firstonly RecId from prodTab where prodTab.ProdId == jobRef;
                    if (prodTab)
                    {
                        jourRegType         = JmgJourRegTypeEnum::MrpQueueBefore;
                    }
                    else
                    {
                        select firstonly RecId from projTab where projTab.ProjId == jobRef;
                        if (projTab)
                        {
                            jourRegType         = JmgJourRegTypeEnum::PrjProject;
                        }
                    }
                }
            }
        }
    }
    else if ((jourRegType == JmgJourRegTypeEnum::Absence) || (jourRegType ==JmgJourRegTypeEnum::BreakReg))
        limitType = true;

    return [(!(limitJourReg || limitJobRef || limitType)),jourRegType,limitStampType,stamptype,module];
}

public void init()
{
    if (! element.args().caller())
    {
        element.form().design().frame(FormFrame::Standard);
    }
    super();
    //filterControl = category;
}

void run()
{
    boolean             filterLookup    = false;
    JmgIpcCategory      jmgIpcCategoryLocal;
    if (element.args().caller())
    {
        callerControl   = SysTableLookup::getCallerStringControl(element.args());
    }

    this.setupRanges();
    filterLookup = SysTableLookup::filterLookupPreRun(callerControl,filterControl,jmgIpcCategory_DS);

    super();

    if (callerControl)
    {
        /*if (filterLookup)
        {
            jmgIpcCategory_DS.research();
            category.filter(callerControl.text());
        }*/
        SysTableLookup::filterLookupPostRun(filterLookup,callerControl.text(),filterControl,jmgIpcCategory_DS);

        if (!filterLookup)
        {
            jmgIpcCategoryLocal = JmgIpcCategory::find(callerControl.text());

            if (jmgIpcCategoryLocal)
            {
                jmgIpcCategory_DS.positionToRecord(jmgIpcCategoryLocal);
            }
        }
    }
}

void setupRanges()
{
    QueryBuildDataSource    qbdsCategory;
    QueryBuildDataSource    qbdsProduction;
    QueryBuildDataSource    qbdsProject;
    QueryBuildDataSource    qbdsAbsence;
    tableId                 inTableId       = this.args().dataset();
    JmgStampTransMap        inTransMap;
    JmgIpcCategory          ipcCategory;
    QueryBuildRange         qbr;
    tableName               inTableName     = tableid2name(inTableId);
    TmpSysQuery             tmpquery;
    JmgStampTypeEnum        stamptype;
    boolean                 limitStampType  = false;
    JmgModuleEnum           module;
    JmgJourRegTypeEnum      jourRegType;
    boolean                 showAll         = true;

    JmgTimecardTrans        jmgTimecardTrans;

    qbdsCategory    = jmgIpcCategory_DS.query().dataSourceTable(tablenum(JmgIpcCategory));
    qbdsProduction  = prodTable_DS.query().dataSourceTable(tablenum(ProdTable));
    qbdsProject     = projTable_DS.query().dataSourceTable(tablenum(ProjTable));
    qbdsAbsence     = hrmAbsenceCodeGroup_DS.query().dataSourceTable(tablenum(HRMAbsenceCodeGroup));

    qbdsCategory.clearRanges();
    qbdsCategory.clearDynalinks();
    qbdsProduction.clearRanges();
    qbdsProduction.clearDynalinks();
    qbdsProject.clearRanges();
    qbdsProject.clearDynalinks();
    qbdsAbsence.clearRanges();
    qbdsAbsence.clearDynalinks();
    switch (inTableId)
    {
        case tablenum(JmgStampJournalTrans) :
        case tablenum(JmgStampTrans)        :
        case tablenum(JmgStampTransMap)     :
            inTransMap.data(this.args().record());
            showAll         = false;
            limitStampType  = true;
            jourRegType     = inTransMap.JourRegType;
            stamptype       = inTransMap.StampType;
            module          = inTransMap.Module;
            break;
        case tablenum(JmgIpcCategory)   :
            ipcCategory.data(this.args().record());
            showAll         = false;
            limitStampType  = true;
            jourRegType     = (ipcCategory.Type == JmgStampTypeEnum::Absence) ? JmgJourRegTypeEnum::Absence     :
                              (ipcCategory.Type == JmgStampTypeEnum::Break)   ? JmgJourRegTypeEnum::BreakReg    : JmgJourRegTypeEnum::IndActivity;
            stamptype       = ipcCategory.Type;
            module          = JmgModuleEnum::IPC;
            break;

        case tablenum(TmpSysQuery)   :
            tmpquery.setTmpData(this.args().record());
            select firstonly tmpquery
                where ((tmpquery.Table_Id == tablenum(JmgStampJournalTrans))    ||
                       (tmpquery.Table_Id == tablenum(JmgStampTrans))           ||
                       (tmpquery.Table_Id == tablenum(JmgIpcActivity))          ||
                       (tmpquery.Table_Id == tablenum(JmgIpcCategory))          ) &&
                      (tmpquery.RangeValue);
            if (tmpquery)
                [showAll,jourRegType,limitStampType,stamptype,module] = element.analyzeTmpQuery(tmpquery);
            break;
        case tablenum(JmgTimecardTrans)   :
            jmgTimecardTrans= this.args().record();
            showAll         = false;
            jourRegType     = jmgTimecardTrans.JourRegType;
            limitStampType  = jourRegType != 0;
            stamptype       = jmgTimecardTrans.stampType();
            module          = jmgTimecardTrans.module();
            break;
    }

    switch (jourRegType)
    {
        case JmgJourRegTypeEnum::IndActivity    :
            stamptype       = JmgStampTypeEnum::Work;
            module          = JmgModuleEnum::IPC;
            break;
        case JmgJourRegTypeEnum::Absence   :
            stamptype       = JmgStampTypeEnum::Absence;
            module          = JmgModuleEnum::IPC;
            break;
        case JmgJourRegTypeEnum::BreakReg   :
            stamptype       = JmgStampTypeEnum::Break;
            module          = JmgModuleEnum::IPC;
            break;
        case JmgJourRegTypeEnum::MrpQueueBefore :
        case JmgJourRegTypeEnum::MrpSetup       :
        case JmgJourRegTypeEnum::MrpProcess     :
        case JmgJourRegTypeEnum::MrpOverlap     :
        case JmgJourRegTypeEnum::MrpTransport   :
        case JmgJourRegTypeEnum::MrpQueueAfter  :
            stamptype       = JmgStampTypeEnum::Work;
            module          = JmgModuleEnum::PROD;
            break;
        case JmgJourRegTypeEnum::PrjProject :
        case JmgJourRegTypeEnum::PrjActivity :
            stamptype       = JmgStampTypeEnum::Work;
            module          = JmgModuleEnum::PROJ;
            break;
    }

    element.selectMode(category);
    if (!showAll)
    {
        tab.showTabs(false);
        switch (module)
        {
            case JmgModuleEnum::IPC :
                productionPage.visible(false);
                projectPage.visible(false);
                if (limitStampType)
                {
                    qbr     = qbdsCategory.addRange(fieldnum(JmgIpcCategory,Type));
                    qbr.status(RangeStatus::Hidden);
                    qbr.value(queryValue(stamptype));
                }
                if (stamptype == JmgStampTypeEnum::Absence)
                {
                    categoryPage.visible(false);
                    element.selectMode(hrmAbsenceCodeGroup_hrmAbsenceCodeGroupId);
                    filterControl = hrmAbsenceCodeGroup_hrmAbsenceCodeGroupId;
                }
                else
                {
                    absencePage.visible(false);
                    element.selectMode(category);
                    filterControl = category;
                }
                break;
            case JmgModuleEnum::PROD :
                prodid.setFocus();
                categoryPage.visible(false);
                projectPage.visible(false);
                absencePage.visible(false);
                element.selectMode(prodId);
                filterControl = prodId;
                break;
            case JmgModuleEnum::PROJ :
                projid.setFocus();
                productionPage.visible(false);
                categoryPage.visible(false);
                absencePage.visible(false);
                element.selectMode(projId);
                filterControl = projId;
                break;
        }
    }
}

void tabChanged(int fromTab, int toTab)
{
    super(fromTab, toTab);

    switch (toTab)
    {
        case #CatePage:
            filterControl = category;
            ///element.selectMode(category);
            break;
        case #ProdPage:
            filterControl = prodId;
            //element.selectMode(prodId);
            break;
        case #ProjPage:
            filterControl = projId;
            //element.selectMode(projId);
            break;
        case #absencePage:
            filterControl = hrmAbsenceCodeGroup_hrmAbsenceCodeGroupId;
            //element.selectMode(HRMAbsenceCodeGroup_hrmAbsenceCodeGroupId);
            break;
    }
    element.selectMode(filterControl);
}
