[Form]
public class JmgPayAgreementLine extends FormRun
{
    QueryBuildRange         rangeDayId;
    QueryBuildRange         rangePayType;
    JmgPayAgreement         callerPayAgreement;

}

void init()
{
    JmgPayAgreementTable    jmgPayAgreementTable;

    super();

    paySpecType.visible(false);

    if (element.args().dataset()== tableNum(JmgPayAgreementTable))
    {
        jmgPayAgreementTable = element.args().record();
        callerPayAgreement = jmgPayAgreementTable.PayAgreement;
        jmgPayAgreementLine_PayAgreement.visible(false);
        identification.visible(false);
    }
    rangeDayId      = jmgPayAgreementLine_ds.query().dataSourceNo(1).addRange(fieldNum(JmgPayAgreementLine,DayId));
    rangeDayId.value(queryValue(JmgDayIdEnum::Monday));
    rangePayType    = jmgPayAgreementLine_ds.query().dataSourceNo(1).addRange(fieldNum(JmgPayAgreementLine,PaySpecType));
    rangePayType.value(queryValue(JmgPaySpecTypeEnum::WorkTime));

    this.initDayOfWeekMenuButton();
    this.initPayTypeMenuButton();
}

private void initDayOfWeekMenuButton()
{
    ProdEnumToMenuButtonsController::init(
        DayOfWeekMenuButton,
        enumNum(JmgDayId),
        formmethodstr(JmgPayAgreementLine, dayOfWeekSelectionChangedButtonHandler),
        this);

    this.dayOfWeekSelectionChanged(JmgDayIdEnum::Monday);
}

private void initPayTypeMenuButton()
{
    ProdEnumToMenuButtonsController::init(
        PayTypeMenuButton,
        enumNum(JmgPaySpecTypeEnumPick),
        formmethodstr(JmgPayAgreementLine, payTypeSelectionChangedButtonHandler),
        this);

    this.payTypeSelectionChanged(JmgPaySpecTypeEnumPick::WorkTime);
}

private void dayOfWeekSelectionChangedButtonHandler(FormCommandButtonControl _selectedControl)
{
    JmgDayId newSelection = str2int(_selectedControl.parameters());
    this.dayOfWeekSelectionChanged(newSelection);
}

private void payTypeSelectionChangedButtonHandler(FormCommandButtonControl _selectedControl)
{
    JmgPaySpecTypeEnumPick newSelection = str2int(_selectedControl.parameters());
    this.payTypeSelectionChanged(newSelection);
}

private void dayOfWeekSelectionChanged(JmgDayId _newSelection)
{
    DayOfWeekMenuButton.text(enum2str(_newSelection));

    rangeDayId.value(queryValue(_newSelection));
    jmgPayAgreementLine_ds.executeQuery();
}

private void payTypeSelectionChanged(JmgPaySpecTypeEnumPick _newSelection)
{
    PayTypeMenuButton.text(enum2str(_newSelection));

    routeOprId.enabled(_newSelection != JmgPaySpecTypeEnumPick::Absence);
    rangePayType.value(queryValue(_newSelection));
    jmgPayAgreementLine_ds.executeQuery();
}

public void openCloseFieldsFixedQuantity()
{
    if (jmgPayAgreementLine.UseConstantPay || jmgPayAgreementLine.ForcePay)
    {
        payConst.enabled(true);
    }
    else
    {
        payConst.enabled(false);
    }
}

JmgPayAgreement returnPayAgreement()
{
    return callerPayAgreement;
}

void setActiveDelimitation()
{
    routeOprId.allowEdit(
        !jmgPayAgreementLine.ProjId && !jmgPayAgreementLine.ActivityNumber &&
        !jmgPayAgreementLine.AbsenceActivityId && !jmgPayAgreementLine.hrmAbsenceCodeId);
    routeOprId.skip(!routeOprId.allowEdit());

    projId.allowEdit(
        !jmgPayAgreementLine.RouteOprId && !jmgPayAgreementLine.AbsenceActivityId &&
        !jmgPayAgreementLine.hrmAbsenceCodeId);
    projId.skip(!projId.allowEdit());

    delimitation_ActivityNumber.allowEdit(jmgPayAgreementLine.ProjId != '');
    delimitation_ActivityNumber.skip(!delimitation_ActivityNumber.allowEdit());
    if (projId.text() == '')
    {
        delimitation_ActivityNumber.text('');
    }

    absenceActivityId.allowEdit(
        !jmgPayAgreementLine.RouteOprId && !jmgPayAgreementLine.ProjId &&
        !jmgPayAgreementLine.ActivityNumber && !jmgPayAgreementLine.hrmAbsenceCodeId);
    absenceActivityId.skip(!absenceActivityId.allowEdit());

    hrmAbsenceCodeId.allowEdit(
        !jmgPayAgreementLine.RouteOprId && !jmgPayAgreementLine.ProjId &&
        !jmgPayAgreementLine.ActivityNumber && !jmgPayAgreementLine.AbsenceActivityId);
    hrmAbsenceCodeId.skip(!hrmAbsenceCodeId.allowEdit());

    delimitation_RatingLevel.allowEdit(jmgPayAgreementLine.SkillId != '');
    delimitation_RatingLevel.skip(!delimitation_RatingLevel.allowEdit());
}

public int active()
{
    int ret;

    ret = super();

    element.setActiveDelimitation();
    element.openCloseFieldsFixedQuantity();

    return ret;
}

display JmgLabelTimeMax timeMaxCountUnit(JmgPayAgreementLine _jmgPayAgreementLine)
{
    JmgPayCountTable payCountTable;

    payCountTable = JmgPayCountTable::find(_jmgPayAgreementLine.PayCountId);

    return JmgProfileSeconds::formattedSeconds(payCountTable.TimeMax,JmgSecondPresentationEnum::Centitime);
}

