    JmgPayCountTable payCountTable;

    payCountTable = JmgPayCountTable::find(_jmgPayAgreementLine.PayCountId);

    return JmgProfileSeconds::formattedSeconds(payCountTable.TimeMin,JmgSecondPresentationEnum::Centitime);
}

