public void lookup(FormControl _formControl, str _filterStr)
{
    SysTableLookup          sysTableLookup;
    Query                   q;
    QueryBuildDataSource    qbds;

    sysTableLookup = SysTableLookup::newParameters(tableNum(JmgPayCountTable), _formControl);
    sysTableLookup.addLookupfield(fieldNum(JmgPayCountTable, PayCountId), true);
    sysTableLookup.addLookupfield(fieldNum(JmgPayCountTable, Description), false);

    q       = new Query();
    qbds    = q.addDataSource(tableNum(JmgPayCountTable));
    qbds.addRange(fieldNum(JmgPayCountTable, PayAgreement)).value(jmgPayAgreementLine.PayAgreement);

    sysTableLookup.parmQuery(q);

    sysTableLookup.performFormLookup();
}

void modified()
{
    JmgPayAgreementLine jmgPayAgreementLineBuf;

    super();

    if (jmgPayAgreementLine.PayCountId)
    {
        select firstonly jmgPayAgreementLineBuf
            where jmgPayAgreementLineBuf.PayCountId   == jmgPayAgreementLine.PayCountId   &&
                  (jmgPayAgreementLineBuf.PaySpecType != jmgPayAgreementLine.PaySpecType  ||
                  jmgPayAgreementLineBuf.DayId        == jmgPayAgreementLine.DayId)       &&
                  jmgPayAgreementLineBuf.PayAgreement == jmgPayAgreementLine.PayAgreement &&
                  jmgPayAgreementLineBuf.RecId        != jmgPayAgreementLine.RecId;

        if (jmgPayAgreementLineBuf)
        {
            if (jmgPayAgreementLineBuf && jmgPayAgreementLineBuf.DayId   == jmgPayAgreementLine.DayId)
            {
                warning("@SYS113928");
            }
            else
            {
                warning("@SYS113929");
            }
            jmgPayAgreementLine.PayCountId = '';
        }
    }
}

