/// <summary>
/// Override lookup method for activity field to not show paid breaks.
/// </summary>
public void lookup()
{
    Query query = new Query();
    QueryBuildDataSource queryBuildDataSource;

    queryBuildDataSource = query.addDataSource(tableNum(JmgIpcActivity));

    //Create an instance of SysTableLookup class. 'this' in the example represents the current form control.
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(JmgIpcActivity), this);
    sysTableLookup.addLookupField(fieldNum(JmgIpcActivity, Activity));
    sysTableLookup.addLookupField(fieldNum(JmgIpcActivity, Category));
    sysTableLookup.addLookupField(fieldNum(JmgIpcActivity, Type));
    sysTableLookup.addLookupField(fieldNum(JmgIpcActivity, JobId));

    //Use a query to retrieve data for the lookup form.
    queryBuildDataSource.addRange(fieldNum(JmgIpcActivity, Type)).value(SysQuery::valueNot(JmgStampTypeEnum::EventCode));

    var dsView = queryBuildDataSource.addDataSource(tableNum(JmgIpcActivity));
    dsView.joinMode(JoinMode::NoExistsJoin);
    dsView.addLink(fieldNum(JmgIpcActivity, RecId), fieldNum(JmgIpcActivity, RecId));
    dsView.addRange(fieldNum(JmgIpcActivity, Type)).value(SysQuery::value(JmgStampTypeEnum::Break));
    dsView.addRange(fieldNum(JmgIpcActivity, BreakPayable)).value(SysQuery::value(NoYes::No));

    sysTableLookup.parmQuery(query);

    //Use the performFormLookup method to open the lookup form.
    sysTableLookup.performFormLookup();
}
