Method.Source:
[Form]
public class JmgPayAgreementOverride extends FormRun
{
    QueryBuildRange         rangePayType;
    JmgStampJournalTable    jmgStampJournalTableIn;
    boolean                 canEditMode;

}

void close()
{
    FormDataSource       ds;

    super();

    ds = FormDataUtil::getFormDataSource(element.args().record());
    ds.reread();
    ds.refresh();
}

void init()
{
    QueryBuildRange rangeWorker;
    QueryBuildRange rangeProfileDate;

    super();

    canEditMode = false;
    getPayAgreement.enabled(false);
    deletePayAgreement.enabled(false);

    jmgStampJournalTableIn = element.args().record();

    if (jmgStampJournalTableIn.Calculated   && !jmgStampJournalTableIn.Approved)
    {
        canEditMode = true;
    }

    rangePayType    = jmgPayAgreementOverrideLine_ds.query().dataSourceNo(1).addRange(fieldnum(JmgPayAgreementOverrideLine,PaySpecType));
    rangePayType.value(queryValue(JmgPaySpecTypeEnumPick::WorkTime));

    rangeWorker         = jmgPayAgreementOverride_DS.query().dataSourceNo(1).addRange(fieldnum(JmgPayAgreementOverride,Worker));
    rangeProfileDate    = jmgPayAgreementOverride_DS.query().dataSourceNo(1).addRange(fieldnum(JmgPayAgreementOverride,ProfileDate));

    rangeWorker.value(queryValue(jmgStampJournalTableIn.Worker));
    rangeProfileDate.value(queryValue(jmgStampJournalTableIn.ProfileDate));

    this.setEnabled();
    this.initPayTypeMenuButton();
}

private void initPayTypeMenuButton()
{
    ProdEnumToMenuButtonsController::init(
        PayTypeMenuButtonGroup,
        enumNum(JmgPaySpecTypeEnumPick),
        formmethodstr(JmgPayAgreementOverride, payTypeSelectionChangedButtonHandler),
        this);

    // Set default pay type selection.
    this.payTypeSelectionChanged(JmgPaySpecTypeEnumPick::WorkTime);
}

public void openCloseFieldsFixedQuantity()
{
    if (jmgPayAgreementOverrideLine.UseConstantPay || jmgPayAgreementOverrideLine.ForcePay)
    {
        payConst.enabled(true);
    }
    else
    {
        payConst.enabled(false);
    }
}

void run()
{
    if (!(element.args().dataset() == tablenum(JmgStampJournalTable)))
        throw error(strfmt("@SYS19096", element.name(), tablestr(JmgStampJournalTable)));

    if (!jmgStampJournalTableIn.Calculated)
        return;

    if ((jmgStampJournalTableIn.Approved) &&
        (!JmgPayAgreementOverride::find(jmgStampJournalTableIn.Worker,jmgStampJournalTableIn.ProfileDate).RecId))
        return;

    super();
}

void setActiveDelimitation()
{
    routeOprId.allowEdit(
        !jmgPayAgreementOverrideLine.ProjId && !jmgPayAgreementOverrideLine.ActivityNumber &&
        !jmgPayAgreementOverrideLine.AbsenceActivityId && !jmgPayAgreementOverrideLine.hrmAbsenceCodeId);
    routeOprId.skip(!routeOprId.allowEdit());

    projId.allowEdit(
        !jmgPayAgreementOverrideLine.RouteOprId && !jmgPayAgreementOverrideLine.AbsenceActivityId &&
        !jmgPayAgreementOverrideLine.hrmAbsenceCodeId);
    projId.skip(!projId.allowEdit());

    delimitation_ActivityNumber.allowEdit(jmgPayAgreementOverrideLine.ProjId != '');
    delimitation_ActivityNumber.skip(!delimitation_ActivityNumber.allowEdit());

    absenceActivityId.allowEdit(
        !jmgPayAgreementOverrideLine.RouteOprId && !jmgPayAgreementOverrideLine.ProjId &&
        !jmgPayAgreementOverrideLine.ActivityNumber && !jmgPayAgreementOverrideLine.hrmAbsenceCodeId);
    absenceActivityId.skip(!absenceActivityId.allowEdit());

    hrmAbsenceCodeId.allowEdit(
        !jmgPayAgreementOverrideLine.RouteOprId && !jmgPayAgreementOverrideLine.ProjId &&
        !jmgPayAgreementOverrideLine.ActivityNumber && !jmgPayAgreementOverrideLine.AbsenceActivityId);
    hrmAbsenceCodeId.skip(!hrmAbsenceCodeId.allowEdit());

    delimitation_RatingLevel.allowEdit(jmgPayAgreementOverrideLine.SkillId != '');
    delimitation_RatingLevel.skip(!delimitation_RatingLevel.allowEdit());
}

public void setEnabled()
{
    boolean enable;
    enable = jmgStampJournalTableIn.Calculated == NoYes::Yes    && jmgStampJournalTableIn.Approved == NoYes::No;

    if (JmgPayAgreementOverride::find(jmgStampJournalTableIn.Worker, jmgStampJournalTableIn.ProfileDate).RecId)
    {
        deletePayAgreement.enabled(enable);
        getPayAgreement.enabled(false);
    }
    else
    {
        deletePayAgreement.enabled(false);
        getPayAgreement.enabled(enable);
    }

    jmgPayAgreementOverride_DS.allowEdit(enable);
    jmgPayAgreementOverride_DS.allowDelete(enable);
    jmgPayAgreementOverrideLine_DS.allowEdit(enable);
    jmgPayAgreementOverrideLine_DS.allowCreate(enable);
    jmgPayAgreementOverrideLine_DS.allowDelete(enable);
}

public void initValue()
{
    super();

    if (!JmgPayAgreementOverride::find(jmgStampJournalTableIn.Worker, jmgStampJournalTableIn.ProfileDate))
    {
        jmgPayAgreementOverrideLine.Worker      = jmgStampJournalTableIn.Worker;
        jmgPayAgreementOverrideLine.ProfileDate = jmgStampJournalTableIn.ProfileDate;
    }
}

public void write()
{
    JmgPayAgreementOverride override;

    override = JmgPayAgreementOverride::find(jmgStampJournalTableIn.Worker, jmgStampJournalTableIn.ProfileDate, true);

    if (!override)
    {
        override.Worker                 = jmgStampJournalTableIn.Worker;
        override.ProfileDate            = jmgStampJournalTableIn.ProfileDate;
        override.PayAgreement           = '';
        override.PayDeductToLateSeconds = 0;

        override.insert();

        jmgPayAgreementOverride_DS.executeQuery();

        element.setEnabled();
    }

    super();
}

public int active()
{
    int ret;

    ret = super();

    element.setActiveDelimitation();
    element.openCloseFieldsFixedQuantity();
    return ret;
}

public void modified()
{
    element.setActiveDelimitation();
    jmgPayAgreementOverrideLine.RatingLevel = 0;
    delimitation_RatingLevel.update();
    super();
}
