public class JmgProdBOMOprNum extends FormRun
{
    Map                     mapRemainInventFinancial;
    Map                     mapAvailPhysical;
    Map                     mapReqQuantity;
    Map                     mapProdStatus;
    QueryBuildDataSource    qbdsProdBOM;
    QueryBuildRange         qbrOprNum;
    QueryBuildRange         qbrProdID;
    ProdId                  lastProdID;

    InventDimCtrl_Frm_Mov   inventDimFormSetup;
    InventMovement          inventMovement;

}

public void init()
{
    FormRun                     formRun;

    mapRemainInventFinancial    = new Map(typeName2Type(extendedTypeStr(RecId)), Types::Real);
    mapAvailPhysical            = new Map(typeName2Type(extendedTypeStr(RecId)), Types::Real);
    mapReqQuantity              = new Map(typeName2Type(extendedTypeStr(RecId)), Types::Real);
    mapProdStatus               = new Map(typeName2Type(extendedTypeStr(RecId)), Types::Enum);

    super();

    formRun = element.args().caller();

    element.updateDesign(InventDimFormDesignUpdate::Init);
    element.switchBetweenButtons();
}

Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

public void lookupOprNum(FormControl _formControl)
{
    SysTableLookup sysTableLookup  = SysTableLookup::newParameters(tableNum(ProdRoute), _formControl);
    Query q = new Query();
    QueryBuildDataSource    qbdsProdRoute   = q.addDataSource(tableNum(ProdRoute));
    QueryBuildRange         qbrProdOrder    = qbdsProdRoute.addRange(fieldNum(ProdRoute,ProdId));

    // Apply filters to datasource here (e.g. production order).
    qbrProdOrder.value(queryValue(lastProdID));

    sysTableLookup.addLookupfield(fieldNum(ProdRoute, OprNum), true);
    sysTableLookup.addLookupfield(fieldNum(ProdRoute, OprId), false);
    // Add additional fields to be shown on lookup.
    sysTableLookup.parmQuery(q);
    sysTableLookup.performFormLookup();
}

public void onFilterChange(int _newValue)
{
    if (_newValue != 0)
    {
        qbrOprNum.value(queryValue(_newValue));
    }
    else
    {
        qbrOprNum.value('');
    }

    prodBOM_DS.executeQuery();
}

public void run()
{
    super();
    this.setupImageListError(imageError);
}

private void setupImageListError(FormWindowControl _formWindowControl)
{
    #JmgIcons

    Imagelist imagelist = new Imagelist(Imagelist::smallIconWidth(), Imagelist::smallIconHeight());

    imagelist.add(new Image(#JMG_IMG_MESSAGE_IMPORTANT));

    if (_formWindowControl)
    {
        _formWindowControl.imageList(imagelist);
    }
}

public void switchBetweenButtons()
{
    JmgJobTable jobTable;

    if (element.args().dataset() == tableNum(JmgJobTable))
    {
        jobTable = element.args().record();

        if (currentJobsButton.enabled())
        {
            qbrProdID.value(queryValue(jobTable.ModuleRefId));
            qbrOprNum.value(queryValue(jobTable.OprNum));
            oprNum.visible(false);
            currentJobsButton.enabled(false);
            allJobsButton.enabled(true);
        }
        else
        {
            qbrProdID.value(queryValue(jobTable.ModuleRefId));
            qbrOprNum.value(SysQuery::valueUnlimited());
            oprNum.visible(true);
            currentJobsButton.enabled(true);
            allJobsButton.enabled(false);
        }

        prodBOM_DS.executeQuery();
    }
    else
    {
        viewGroup.enabled(viewGroup.visible(false));
    }
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    InventDimParm   inventDimParmEnabled;

    inventMovement = InventMovement::construct(prodBOM);

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init          :
            inventMovement = InventMovement::construct(prodBOM);

            if (!inventDimFormSetup)
                inventDimFormSetup  = InventDimCtrl_Frm_Mov::newFromFormMovement(element,inventMovement);

        case InventDimFormDesignUpdate::Active        :
            inventDimFormSetup.formActiveSetup(inventMovement.inventDimGroupSetup(),inventMovement);

            inventDimParmEnabled = inventDimFormSetup.parmDimParmEnabled();
            inventDimParmEnabled.InventLocationIdFlag = (prodBOM.WrkCtrConsumption ? NoYes::No : NoYes::Yes);
            inventDimFormSetup.parmDimParmEnabled(inventDimParmEnabled);

            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange   :
            inventDimFormSetup.formActiveSetup(inventMovement.inventDimGroupSetup(),inventMovement);
            inventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

display RouteOprId routeOprId(ProdBOM _prodBOM)
{
    return _prodBOM.prodRoute().OprId;
}

public int active()
{
    int ret;

    ret = super();

    lastProdID = prodBOM.ProdId;

    ProdOrderInfoGroup.caption(strFmt("@SYS312531", "@SYS89639",lastProdID));
    element.updateDesign(InventDimFormDesignUpdate::Active);

    return ret;
}

public void init()
{
    super();

    qbdsProdBOM = prodBOM_DS.query().dataSourceTable(tableNum(ProdBOM));
    qbdsProdBOM.clearDynalinks();
    qbrOprNum   = qbdsProdBOM.addRange(fieldNum(ProdBOM,OprNum));
    qbrProdID   = qbdsProdBOM.addRange(fieldNum(ProdBOM,ProdId));
    element.updateDesign(InventDimFormDesignUpdate::Init);
}

display ProdStatus prodStatus(ProdBOM _prodBOM)
{
    if (! mapProdStatus.exists(_prodBOM.RecId))
        mapProdStatus.insert(_prodBOM.RecId,ProdTable::find(_prodBOM.ProdId).ProdStatus);

    return mapProdStatus.lookup(_prodBOM.RecId);
}

display RouteOprQtyToOpr reqQty(ProdBOM _prodBOM)
{
    if (! mapReqQuantity.exists(_prodBOM.RecId))
        mapReqQuantity.insert(_prodBOM.RecId,ProdRoute::find(_prodBOM.ProdId,_prodBOM.OprNum,RouteOprPriority::Primary).CalcQty);

    return mapReqQuantity.lookup(_prodBOM.RecId);
}

public void cursorNotify(int _event)
{
    ProdBOM     prodBOMCache;
    Map         mapProdBOM;

    container   conMapRemainInventFinancial;
    container   conMapAvailPhysical;

    super(_event);

    if (_event == 0 || _event == 1)
    {
        prodBOMCache = prodBOM_DS.getFirst(0,false);
        while (prodBOMCache)
        {
            if (! mapRemainInventFinancial.exists(prodBOMCache.RecId))
            {
                if (! mapProdBOM)
                    mapProdBOM = new Map(typeName2Type(extendedTypeStr(RecId)), Types::Record);

                mapProdBOM.insert(prodBOMCache.RecId,prodBOMCache);
            }

            prodBOMCache = prodBOM_DS.getNext();
        }

        if (mapProdBOM)
            [conMapRemainInventFinancial,conMapAvailPhysical] = ProdBOM::prodBOMOprNumDisplayCache(mapProdBOM.pack(),
                                                                                                   mapRemainInventFinancial.pack(),
                                                                                                   mapAvailPhysical.pack());
        if (conMapRemainInventFinancial)
            mapRemainInventFinancial = Map::create(conMapRemainInventFinancial);

        if (conMapAvailPhysical)
            mapAvailPhysical         = Map::create(conMapAvailPhysical);
    }
}

display InventQty remainInventFinancial(ProdBOM _prodBOM)
{
    if (! mapRemainInventFinancial.exists(_prodBOM.RecId))
        mapRemainInventFinancial.insert(_prodBOM.RecId,_prodBOM.remainInventFinancial());

    return mapRemainInventFinancial.lookup(_prodBOM.RecId);
}

display InventQtyAvailPhysicalDim availPhysicalItem(ProdBOM _prodBOM)
{
    if (! mapAvailPhysical.exists(_prodBOM.RecId))
        mapAvailPhysical.insert(_prodBOM.RecId,_prodBOM.availPhysical());

    return mapAvailPhysical.lookup(_prodBOM.RecId);
}

