public void enableFeedbackValidationFields()
{
    boolean enableFields;
    feedbackQtyValidation_ValidateFeedbackQuantity.enabled(jmgProdParametersDim.ReportFinishStatus != JmgProdStatusUpdateReportFinished::No);

    enableFields = jmgProdParametersDim.ReportFinishStatus != JmgProdStatusUpdateReportFinished::No  && jmgProdParametersDim.ValidateFeedbackQuantity == NoYes::Yes;

    feedbackQtyValidation_FeedbackValidateMethod.enabled(enableFields);
    feedbackQtyValidation_FeedbackAcceptOverDelivery.enabled(enableFields);
    feedbackQtyValidation_FeedbackOverDeliveryPercentage.enabled(enableFields && jmgProdParametersDim.FeedbackAcceptOverDelivery == NoYes::Yes);
    feedbackQtyValidation_FeedbackAcceptUnderDelivery.enabled(enableFields);
    feedbackQtyValidation_FeedbackUnderDeliveryPercentage.enabled(enableFields && jmgProdParametersDim.FeedbackAcceptUnderDelivery == NoYes::Yes);
}

public void enableOperationsFields()
{
    boolean enableType;
    enableType = jmgProdParameters.StampLevel == JmgStampLevel::Job;

    registerOn_AllowProdQueueBefore.enabled(enableType);
    registerOn_AllowProdSetup.enabled(true);
    registerOn_AllowProdProcess.enabled(true);
    registerOn_AllowProdOverlap.enabled(enableType);
    registerOn_AllowProdTransport.enabled(enableType);
    registerOn_AllowProdQueueAfter.enabled(enableType);
}

public void enableReportFinishFields()
{
    boolean enableGroups;
    reportFinish_ReportFinishUseSFCParameters.enabled(jmgProdParametersDim.ReportFinishStatus != JmgProdStatusUpdateReportFinished::No);

    enableGroups = jmgProdParametersDim.ReportFinishStatus != JmgProdStatusUpdateReportFinished::No &&
                   jmgProdParametersDim.ReportFinishUseSFCParameters    == NoYes::Yes;

    reportFinishRouteCardJournal.enableChilds(enableGroups);
    reportFinishPickingListJournal.enableChilds(enableGroups);
    reportFinishReportAsFinishedJournal.enableChilds(enableGroups);
}

public void enableStartupFields()
{
    boolean enableGroups;
    startUpJournals_StartUpUseSFCParameters.enabled(jmgProdParametersDim.StartUpStatus != JmgProdStatusUpdate::No);

    enableGroups = jmgProdParametersDim.StartUpStatus              != JmgProdStatusUpdate::No &&
                   jmgProdParametersDim.StartUpUseSFCParameters    == NoYes::Yes;

    startUpStart.enableChilds(enableGroups);
    startUpRouteCardJournal.enableChilds(enableGroups);
    startUpPickingListJournal.enableChilds(enableGroups);

    startUpStart_StartUpProduction.enabled(jmgProdParametersDim.StartUpStatus == JmgProdStatusUpdate::Qty);
}

public void enableStartupValidationFields()
{
    boolean enableFields;
    startupQtyValidation_ValidateStartupQuantity.enabled(jmgProdParametersDim.StartUpStatus == JmgProdStatusUpdate::Qty);

    enableFields = jmgProdParametersDim.StartUpStatus == JmgProdStatusUpdate::Qty && jmgProdParametersDim.ValidateStartupQuantity == NoYes::Yes;

    startupQtyValidation_StartupValidateMethod.enabled(enableFields);
    startupQtyValidation_StartupAcceptOverDelivery.enabled(enableFields);
    startupQtyValidation_StartupOverDeliveryPercentage.enabled(enableFields && jmgProdParametersDim.StartupAcceptOverDelivery == NoYes::Yes);
}

public void init()
{
    super();

    JmgProdParameters::find();
    JmgProdParametersDim::find(InventDim::inventDimIdBlank());

    this.updateDesign(InventDimFormDesignUpdate::Init);

    this.initSiteFromArgs();
}

/// <summary>
/// Determines from this.args what mode the form should be set in: Either to show site specific parameters or to
/// only show company default parameters.
/// </summary>
void initSiteFromArgs()
{
    QueryBuildDataSource    qbs;
    QueryBuildRange         rangeDimId;

    companySpecificParameters = !(this.args().parmEnum());

    //Get the query object and add a new range
    qbs         = jmgProdParametersDim_ds.query().dataSourceTable(tableNum(JmgProdParametersDim));
    rangeDimId  = qbs.addRange(fieldNum(JmgProdParametersDim,InventDimId));

    if (!companySpecificParameters)
    {
        //Only show site specific parameters
        overview.visible(true);

        rangeDimId.value(SysQuery::valueNot(InventDim::inventDimIdBlank()));
    }
    else
    {
        //Only show company default parameters
        overview.visible(false);

        rangeDimId.value(SysQuery::value(InventDim::inventDimIdBlank()));
    }

    //Allow edit is determined by if we are dealing with site specific parameters or not
    jmgProdParameters_ds.allowEdit(companySpecificParameters);
    jmgProdParametersDim_ds.allowCreate(!companySpecificParameters);
}

/// <summary>
/// Exposes the private inventDimFormSetup object.
/// </summary>
/// <returns>
/// Object
/// </returns>
/// <remarks>
/// The private inventDimFormSetup object.
/// </remarks>
Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

public void run()
{
    super();

    element.enableStartupFields();
    element.enableReportFinishFields();
    element.enableOperationsFields();
    element.enableStartupValidationFields();
    element.enableFeedbackValidationFields();

    jmgProdParametersDim_DS.allowCreate(!companySpecificParameters && jmgProdParameters.ProdParamInventDimLookup == ProdParamInventDimLookup::Site);
    jmgProdParametersDim_DS.allowDelete(!companySpecificParameters && jmgProdParameters.ProdParamInventDimLookup == ProdParamInventDimLookup::Site);
}

/// <summary>
/// Sets the caption to either "Production parameters by site - Site: site1"
/// or just "Production parameters" depending on
/// what mode the form is in.
/// </summary>
void setCaption()
{
    str     caption;

    if (!companySpecificParameters)
    {
        //Change caption to
        //Production parameters by site - Site: site1
        caption = "@SCM:ProdOrderDefaultsBySite";
        if (inventDim.InventSiteId)
        {
            caption += strFmt("@SYS109019",fieldPName(InventDim,InventSiteId),inventDim.InventSiteId);
        }
    }
    else
    {
        //Production parameters
        caption = "@SCM:ProdOrderDefaults";
    }

    this.design().caption(caption);
}

/// <summary>
/// This method is used in connection with showing the inventory dimensions.
/// </summary>
/// <param name="_mode">
/// Should either be InventDimFormDesignUpdate::Init or InventDimFormDesignUpdate::Active.
/// </param>
/// <exception cref="Exception::Error">
/// The parameter expected should either be InventDimFormDesignUpdate::Init or InventDimFormDesignUpdate::Active
/// </exception>
void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm   inventDimParm;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init        :

            if (! inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl::newFromForm(element);
            }

        case InventDimFormDesignUpdate::Active      :

            //Make sure that only the site dimension are shown
            inventDimParm.InventSiteIdFlag = NoYes::Yes;

            inventDimFormSetup.parmDimParmVisibleGrid(inventDimParm);
            inventDimFormSetup.parmDimParmEnabled(inventDimParm);
            inventDim_ds.allowEdit(jmgProdParametersDim.RecId == 0);

            inventDimFormSetup.formSetControls(true);
            break;

        default :
            throw error(strFmt("@SYS54195",funcName()));
    }
}

public void modified()
{
    super();

    element.enableOperationsFields();
}

public void performJournalLookup(FormControl _formControl)
{
    Query                   q;
    QueryBuildDataSource    qbds;
    SysTableLookup          sysTableLookup;
    q = new Query();
    qbds = q.addDataSource(tableNum(ProdJournalName));
    if (jmgProdParameters.StampLevel == JmgStampLevel::Route)
    {
        qbds.addRange(fieldNum(ProdJournalName, JournalType)).value(queryValue(ProdJournalType::RouteCard));
    }
    else
    {
        qbds.addRange(fieldNum(ProdJournalName, JournalType)).value(queryValue(ProdJournalType::JobCard));
    }

    sysTableLookup = SysTableLookup::newParameters(tableNum(ProdJournalName), _formControl);
    sysTableLookup.parmQuery(q);
    sysTableLookup.addLookupfield(fieldNum(ProdJournalName, JournalNameId), true);
    sysTableLookup.addLookupfield(fieldNum(ProdJournalName, Description), false);

    sysTableLookup.performFormLookup();
}

public void write()
{
    jmgProdParametersDim.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;
    super();
}

public void create(boolean _append = false)
{
    int         i;

    super(_append);

    //Set focus to the first visible control in the grid on the Overview tab
    for (i = 1; i<= inventoryDimensions.controlCount(); i++)
    {
        if (inventoryDimensions.controlNum(i).visible())
        {
            inventoryDimensions.controlNum(i).setFocus();
            break;
        }
    }
}

public int active()
{
    int ret;

    ret = super();

    element.setCaption();

    element.updateDesign(InventDimFormDesignUpdate::Active);

    element.enableFeedbackValidationFields();
    element.enableStartupValidationFields();
    element.enableReportFinishFields();
    element.enableStartupFields();

    return ret;
}

