private boolean isSameResourcesSelected()
{
    JmgJobTable     jmgJobTableLocal;
    WrkCtrIdBase    wrkCtrId;

    if (this.recordsMarked().lastIndex() > 1)
    {
        jmgJobTableLocal = this.getFirst(1);

        wrkCtrId = jmgJobTableLocal.WrkCtrId;

        jmgJobTableLocal = this.getNext();

        while (jmgJobTableLocal)
        {
            if (jmgJobTableLocal.WrkCtrId != wrkCtrId)
            {
                return false;
            }

            jmgJobTableLocal = this.getNext();
        }
    }

    return true;
}

protected void initialize()
{
    super();

    str                 controlText = '';            
    enumId              enumId = enumnum(ProdStatus);                       


    if (element.args().caller())
    {
        FormStringControl callerControl = SysTableLookup::getCallerStringControl(element.args());
        controlText = callerControl.text();
    }

    SysDictEnum dictEnum = new SysDictEnum(enumId);
    str                 enumText;

    ProdStatusComboBox.add("");

    for (int i = 0; i < dictEnum.values(); i++)
    {

        ProdStatus prodStatus = dictEnum.index2Value(i);
        enumText = enum2str(prodStatus);

        ProdStatusComboBox.add(enumText);
        if (enumText == controlText)
        {
            ProdStatusComboBox.selection(i + 1);
        }
    }
}

public boolean modified()
{    
    boolean ret;

    ret = super();

    if (ret)
    {
        QueryBuildDataSource ds = ProdTable_ds.queryBuildDataSource();
        ds.clearRange(fieldNum(ProdTable, ProdStatus));
        QueryBuildRange localProdStatusRange =  ds.addRange( fieldNum(ProdTable, ProdStatus));

        if(this.selection() != 0)
        {
            localProdStatusRange.value(queryValue(this.selection() - 1));
        }
        else
        {
            localProdStatusRange.value(ProdStatusSysQueryRangeUtil::prodStatusBetweenScheduledAndReportedFinished());
        }

        JmgJobTable_ds.executeQuery();
    }

    return ret;
}

