display JmgLabelSecondsPayOvertime getOvertime()
{
    JmgStampJournalTable jmgStampJournalTable;
    JmgTermReg           jmgTermReg;

    JmgDateTime currentDateTime = JmgProfileSeconds::getJmgTermRegCurrentTime();

    select maxof(ProfileDate) from jmgTermReg
        where jmgTermReg.Worker         == curWorker
           && jmgTermReg.RegType        == JmgTermRegType::ClockIn
           && jmgTermReg.RegDateTime    <= currentDateTime;

    if (jmgTermReg.ProfileDate)
    {
        select firstonly PayOverTimeSeconds from jmgStampJournalTable
            where jmgStampJournalTable.Worker         == curWorker
               && jmgStampJournalTable.ProfileDate    == jmgTermReg.ProfileDate;
    }

    return JmgProfileSeconds::formattedSeconds(jmgStampJournalTable.PayOverTimeSeconds);
}

