public void init()
{
    jmgParameters = JmgParameters::find();

    controller = JmgRegistrationForm::construct(this);
    jmgRegistrationSetup = controller.loadRegistrationUserSetup();

    if (!jmgRegistrationSetup)
    {
        // We can't proceed opening the form without the registration setup, close the form
        element.close();
    }
    super();

    if (!element.closed())
    {
        reqPassword   = jmgParameters.UsePINcode;

        passTextField.visible(passTextField.enabled(reqPassword));

        JmgStampJournalTrans::imageListModule(grid_imageModule);

        xSysLastValue::getLast(this);

        if (!this.promptForResource())
        {
            // We can't proceed opening the form without a resource set, close the form
            element.close();
        }
    }
}

