void insertText(str _in) //_in = string of letters
{
    str             tempChar;

    tempChar = subStr(_in,1,1);

    feedbackNum += tempChar;
    if (feedbackField)
        feedbackField.text(feedbackNum);
}

edit JmgJobId jobId(boolean _set, JmgJobId _jobId)
{
    JmgJobTable jmgJobTableLookup;

    if (_set && _jobId)
    {
        jmgJobTableLookup = JmgJobTable::find(_jobId);
        if (jmgJobTableLookup)
        {
            if (useScanner && jmgJobTableLookup.canHaveStartQuantity())
            {
                controller.registerJobStartQtyScanner(jmgJobTableLookup);
            }
            else
            {
                controller.jobMarked(true,jmgJobTableLookup,NoYes::Yes, true);
            }

            controller.beginMarkedJobs();
            this.setFocusOnControl(scannerInput);
        }
        else
        {
            throw error(strFmt("@SYS39813",_jobId));
        }
    }
    return '';
}

