void login()
{
    // The logInBtn button is the default action on the form.
    // When the Enter key is pressed and the selected control is not a filter, lookup etc. this method is called.
    // This check exists to ensure that the user has entered a value into the workerTextField before login is attempted.
    if (workerTextField.text() != '')
    {
        boolean                 usePIN = jmgParameters.UsePINcode;
        JmgEmployee             emplTbl = JmgRegistrationAuthentication::authenticate(workerTextField.text(), passTextField.text(), this.useBadgeID(), usePIN);
        JmgRegistrationSetup    employeeSetup;

        if (emplTbl==null)
        {
            element.clearInputFields();
            feedbackNum = '';
            return;
        }

        curWorker       = emplTbl.Worker;
        userApproved    = true;

        // Prompt for action
        if (!element.promptForAction(curWorker))
        {
            element.clearInputFields();
            curWorker = 0;
            return;
        }

        using(var activityContext = JmgRegistrationInstrumentationActivities::initFormParms(this.getOrCreateLogger().registration().login(curWorker), formStr(JmgRegistration)))
        {
            // Read employee specific registration configuration

            employeeSetup = JmgRegistrationSetup::find(JmgEmployee::find(curWorker).RegistrationSetupId);

            if (controller.setupRegistrationForm(
                curWorker,
                employeeSetup ? employeeSetup : jmgRegistrationSetup,
                true,
                DateTimeUtil::newDateTime(JmgProfileSeconds::getSysDate(), JmgProfileSeconds::getSysTime())) == 0)
            {
                return;
            }

            // The client has already chosen the action pane tab page to display, but we may have hidden it inside setupRegistrationForm.
            // We need to find the first visible tab page and expand that one instead.
            for (int i = 1; i <= ActionPane.controlCount(); i++)
            {
                if (ActionPane.controlNum(i) is FormActionPaneTabControl && ActionPane.controlNum(i).visible())
                {
                    element.SwitchActionPaneTab(ActionPane.controlNum(i));
                    break;
                }
            }

            if (controller.isActionPaneVisible())
            {
                element.updateLayout(employeeSetup ? employeeSetup : jmgRegistrationSetup);
                this.refreshFormPartByName(formstr(JmgNotifications));
                this.refreshFormPartByName(formstr(JmgCurrentActivity));
                this.refreshFormPartByName(formstr(JmgUserStatus));

                element.scrSwitch(true,true);
            }

            // Stop any breaks
            controller.endBreak(jmgRegistrationSetup);
        }
    }
}

