public boolean promptForAction(JmgWorkerRecId _worker)
{
    Args                    args;
    FormRun                 requestActionDialog;
    Object                  object;
    JmgRegistrationSetup    employeeSetup;
    JmgRegistrationSetup    currRegistrationSetup;
    boolean                 canPromptForAction = true;
    JmgEmployee             employee           = JmgEmployee::find(_worker);
    Struct                  actionDialogParameters = new Struct('str prodUnitId; str wrkCtrId');

    if (!employee)
    {
        return true;
    }

    employeeSetup         = JmgRegistrationSetup::find(JmgEmployee::find(curWorker).RegistrationSetupId);
    currRegistrationSetup = employeeSetup ? employeeSetup : jmgRegistrationSetup;

    if (   !currRegistrationSetup.ActionPaneSetupID
        && !currRegistrationSetup.ipcGridSetupID
        && !currRegistrationSetup.ProdGridSetupID
        && !currRegistrationSetup.ProjGridSetupID)
    {
        canPromptForAction = false;
    }

    if (canPromptForAction && employee.PromptForAction == NoYes::Yes)
    {
        args   = new Args(formStr(JmgMESRequestActionDialog));
        args.caller(element);
        args.parm(curWrkCtrId);
        actionDialogParameters.value('prodUnitId', curProdUnitId);
        actionDialogParameters.value('wrkCtrId', curWrkCtrId);
        args.parmObject(actionDialogParameters);
        requestActionDialog = classfactory.formRunClass(args);
        requestActionDialog.run();
        object = requestActionDialog;
        object.parmWorker(_worker);

        requestActionDialog.wait();

        curWrkCtrId = object.selectedResource(false, '');
        if (!curWrkCtrId)
        {
            curWrkCtrId = object.selectedResourceGroup(false, '');
        }
        curProdUnitId = object.selectedProdUnitId(false, '');
        return object.doJobRegistrationClicked();
    }
    else
    {
        curWrkCtrId = terminalWrkCtrId;
        curProdUnitId = terminalProdUnitId;
    }
    return true;
}

public boolean promptForResource()
{
    Args        args;
    FormRun     requestActionDialog;
    Object      object;
    boolean     clickedJobReg = true;
    Struct      actionDialogParameters = new Struct('str prodUnitId; str wrkCtrId');

    if (controller.parmJmgRegistrationUserSetup().ShowJobQueue)
    {
        args   = new Args(formStr(JmgMESRequestActionDialog));
        args.caller(element);
        args.parm(curWrkCtrId ? curWrkCtrId : curProdUnitId);
        args.parmEnum(NoYes::Yes);
        actionDialogParameters.value('prodUnitId', curProdUnitId);
        actionDialogParameters.value('wrkCtrId', curWrkCtrId);
        args.parmObject(actionDialogParameters);
        requestActionDialog = classfactory.formRunClass(args);

        requestActionDialog.run();
        requestActionDialog.wait();

        object = requestActionDialog;
        clickedJobReg = object.doJobRegistrationClicked();

        if (!object.isDialogCancelled())
        {
            curWrkCtrId = object.selectedResource(false, '');
            if (!curWrkCtrId)
            {
                curWrkCtrId = object.selectedResourceGroup(false, '');
            }
            curProdUnitId = object.selectedProdUnitId(false, '');

            terminalWrkCtrId = curWrkCtrId;
            terminalProdUnitId = curProdUnitId;

            if (!clickedJobReg)
            {
                Box::stop("@SCM:CannotOpenJmgRegistrationWithoutFilter");
            }
        }

        xSysLastValue::saveLast(this);
    }
    element.design().caption(strFmt("@SCM:JmgJobCardResource",WrkCtrTable::find(curWrkCtrId).Name));

    return clickedJobReg;
}

public void run()
{
    super();

    if (!element.closed())
    {
        PartList pL = new PartList(element);

        for (int plc = 1; pLc <= pL.partCount(); plc++)
        {
            Object factBox = pL.getPartById(pLc);
            if (factBox && factBox.name() == formStr(JmgNotifications))
            {
                factBox.forceViewAllButton();
            }
        }

        useScanner = jmgRegistrationSetup.Scanner;

        ScanGroup.visible(useScanner);

        controller.requestLogin(true);

        controller.registerAllGridFields(hiddenGrid);
        controller.registerAllPreviewFields(hiddenPreviewGroup);

        this.setEnterIdText();
    }
}

public void scrSwitch(boolean _force = false, boolean _val = false)
{
    ActionPane.enabled(ActionPane.visible(_force ? _val : !ActionPane.visible()));
    loginScreen.enabled(loginScreen.visible(_force ? !_val : !loginScreen.visible()));
    this.enableLockScreenButton();
    JobGrid.enabled(_force ? _val : !JobGrid.enabled());
    JobGrid.visible(_force ? _val : !JobGrid.visible());
    PreviewTab.enabled(_force ? _val : !PreviewTab.enabled());
    PreviewTab.visible(_force ? _val : !PreviewTab.visible());
    JobFilter.enabled(_force ? _val : !JobFilter.enabled());
    JobFilter.visible(_force ? _val : !JobFilter.visible());
    JobGridFilter.enabled(_force ? _val : !JobGridFilter.enabled());
    JobGridFilter.visible(_force ? _val : !JobGridFilter.visible());
    this.setFormPartVisibilityByName(formstr(JmgUserStatus), !loginScreen.visible() && jmgRegistrationSetup.ShowStatus != JmgFastTabAppearance::Hidden);
    this.setFormPartVisibilityByName(formstr(JmgCurrentActivity), !loginScreen.visible() && jmgRegistrationSetup.ShowCurrentActivities != JmgFastTabAppearance::Hidden);

    if (!_val)
    {
        loginScreen.setFocus();
        this.refreshFormPartByName(formstr(JmgNotifications));
        element.clearInputFields();
        element.setFocusOnControl(workerTextField);
    }
    else
    {
        if (useScanner)
        {
            element.setFocusOnControl(scannerInput);
        }
    }
}

