void showCurrentActivities()
{
    JmgTermJob  jmgTermJob = controller.getFirstActiveJob();

    this.removeProjValEmplProjSetupRange();
    jmgJobTable_DS.queryCurrentActivities(curWrkCtrId, curProdUnitId, curInventSiteId);

    if (jmgTermJob.isValidJobId())
    {
        this.updateFormLayoutOnJobModule(jmgTermJob.module());
    }
    else
    {
        jmgJobTable_ModuleRefId.label("@SYS39775");
        element.design().caption("@SYS301946");
    }
}

    void showProductionJobs()
    {
		// The current JobBundler logic empties and resets the bundle type when switching away from production view.
		// Resetting the new jobs and bundleType to empty when going into the production view, resembles this behavior.
        controller.removeNewJobsFromBundleAndForceEmptyBundleType();
        this.removeProjValEmplProjSetupRange();
        jmgJobTable_DS.queryProduction(curWrkCtrId, curProdUnitId, curInventSiteId);
        this.updateFormLayoutOnJobModule(JmgModuleEnum::PROD);
    }

void showProject()
{
    ResourceRecId resource = ResourceFacade::findOrCreateByWorker(JmgEmployee::find(curWorker).Worker);
    if (curWorker)
    {
        if (ResourceFacade::validateProjectResource(resource) &&
            ProjParameters::find().ValidationEmplProj != ProjValParameter::None)
        {
            qbdsProjValEmplProjSetup = qbdsJmgJobTable.addDataSource(tableNum(ProjValEmplProjSetup));
            qbdsProjValEmplProjSetup.addLink(fieldNum(JmgJobTable, ModuleRefId), fieldNum(ProjValEmplProjSetup, ProjId));
            qbdsProjValEmplProjSetup.addRange(fieldNum(ProjValEmplProjSetup, Resource)).value((strFmt("%1, %2", 0, resource)));
            qbdsProjValEmplProjSetup.joinMode(JoinMode::ExistsJoin);
            qbdsProjValEmplProjSetupGroup = qbdsProjValEmplProjSetup.addDataSource(tableNum(ProjValEmplProjSetup));
            qbdsProjValEmplProjSetupGroup.addLink(fieldNum(ProjValEmplProjSetup, GroupId), fieldNum(ProjValEmplProjSetup, GroupId));
            qbdsProjValEmplProjSetupGroup.joinMode(JoinMode::ExistsJoin);
            qbdsProjValEmplProjSetupGroup.addRange(fieldNum(ProjValEmplProjSetup, Resource)).value(int642str(resource));
        }
    }

    jmgJobTable_DS.queryProject();
    this.updateFormLayoutOnJobModule(JmgModuleEnum::PROJ);
}

/// <summary>
/// Changes the expanded tab on the action pane.
/// </summary>
/// <param name = "_actionPaneTab">The tab to expand.</param>
public void SwitchActionPaneTab(FormActionPaneTabControl _actionPaneTab)
{
    if (_actionPaneTab)
    {
        element.setActive();
        element.selectControl(_actionPaneTab);
        ActionPane.setLastActiveTabName(_actionPaneTab.name());

        // We need to refresh the action pane manually because the content of the already active visible tab 'Tab1' is changed by code.
        // If this tab was pinned it is opened during form init and doesn`t have any controls so the content of previous tab is displayed.
        // The only way to do this is to call the setIsPinnedOpen method to call RaisePropertyChange of a FormActionPaneControl.
        if (ActionPane.isPinnedOpen())
        {
            ActionPane.setIsPinnedOpen(ActionPane.isPinnedOpen());
        }
    }
}

