public boolean unpack(container _packedClass)
{
    int version;
    boolean ret;

    version = conPeek(_packedClass, 1);

    switch (version)
    {
        case #CurrentVersion:
            [version, #CurrentList] = _packedClass;
            ret = true;
            break;
        default:
            ret = false;
            break;
    }
    return ret;
}

protected void updateFormLayoutOnJobModule(JmgModuleEnum _module)
{
    switch (_module)
    {
        case JmgModuleEnum::IPC:
            jmgJobTable_ModuleRefId.label("@SYS39775");
            element.design().caption("@SYS38379");
            break;

        case JmgModuleEnum::PROD:
            jmgJobTable_ModuleRefId.label("@SYS89639");
            element.design().caption(strFmt("@SYS309679", WrkCtrTable::find(curWrkCtrId).Name));
            break;

        case JmgModuleEnum::PROJ:
            jmgJobTable_ModuleRefId.label("@SYS39770");
            element.design().caption("@SYS324390");
            break;
    }
}

void updateLayout(JmgRegistrationSetup  _setup = jmgRegistrationSetup)
{
    void collapseExpandTabPage(FormTabPageControl formTabPage, boolean expand)
    {
        if (expand)
        {
            formTabPage.expand();
        }
        else
        {
            formTabPage.collapse();
        }
    }

    jmgRegistrationSetup = _setup;

    this.setFormPartVisibilityByName(formstr(JmgCurrentActivity), !loginScreen.visible() && jmgRegistrationSetup.ShowCurrentActivities != JmgFastTabAppearance::Hidden);
    this.setFormPartVisibilityByName(formstr(JmgUserStatus), !loginScreen.visible() && jmgRegistrationSetup.ShowStatus != JmgFastTabAppearance::Hidden);
    this.setFormPartVisibilityByName(formstr(JmgNotifications), jmgRegistrationSetup.ShowMessages != JmgFastTabAppearance::Hidden);
    this.setFormPartVisibilityByName(formstr(JmgClock), jmgRegistrationSetup.ShowStatus != JmgFastTabAppearance::Hidden);

    this.setupPreviewTab(jmgRegistrationSetup);

    this.refreshFormPartByName(formstr(JmgCurrentActivity));
    this.refreshFormPartByName(formstr(JmgUserStatus));
    jmgJobTable_DS.executeQuery();
}

private FormPartReferenceControl getFormPartByName(str _partName)
{
    PartList list = new PartList(element);
    for (int i = 1; i <= list.partCount(); i++)
    {
        FormPartReferenceControl part = list.getPartControlById(i);
        if (part.getPartFormRun().name() == _partName)
        {
            return part;
        }
    }

    return null;
}

private void refreshFormPartByName(str _partName)
{
    FormPartReferenceControl part = this.getFormPartByName(_partName);
    var refreshable = part.getPartFormRun() as IRefreshable;
    if (refreshable)
    {
        refreshable.refresh();
    }
}

private void setFormPartVisibilityByName(str _partName, boolean _visible)
{
    FormPartReferenceControl part = this.getFormPartByName(_partName);

    if (part)
    {
        part.visible(_visible);
    }
}

