public void querySetDefaultRanges()
{
    qbrProdWrkCtrId.value('');
    qbrProdResourceGroup.value('');
    qbrProdProdUnit.value('');
    qbrProdInventSiteId.value('');
    qbrJobId.value('');
    qbrWorker.value('');

    qbdsWrkCtrTableIndividualView.joinMode(JoinMode::OuterJoin);
}

void queryCurrentActivities(str _wrkCtrId = '', str _prodUnitId = '', str _inventSiteId = '')
{
    container               jobIds;
    int                     i;
    str                     jobIdQuery;
    JmgTermJob              jobTemp;

    qbrProdJobType.value(SysQuery::valueUnlimited());

    this.querySetDefaultRanges();

    jobTemp = controller.getFirstActiveJob();

    // Only in case of production jobs we need to have the inner join to the WrkCtrResourceAndGroupView view
    // in order to retrive resources and resource groups.
    // In all other case we need to have outer join to the WrkCtrResourceAndGroupView view.
    if (jobTemp.isValidJobId() && (jobTemp.module() == JmgModuleEnum::PROD))
    {
        qbdsWrkCtrResourceAndGroupView.joinMode(JoinMode::InnerJoin);
    }
    else
    {
        qbdsWrkCtrResourceAndGroupView.joinMode(JoinMode::OuterJoin);
    }

    jobIds = controller.getCurrJobIdsCon();

    jobIdQuery = '';

    for (i = 1; i <= conLen(jobIds);i++)
    {
        jobIdQuery = queryRangeConcat(jobIdQuery, queryValue(conPeek(jobIds,i)));
    }

    if (conLen(jobIds) > 0)
    {
        qbrJobId.value(jobIdQuery);
    }
    else
    {
        qbrJobId.value(SysQuery::valueEmptyString());
    }

    qbrIPCActivities.value(queryRange(JmgSignInOut::SignIn, JmgSignInOut::SwitchBundleMode));

    jmgJobTable_DS.executeQuery();
}

void queryJobs(WrkCtrId _wrkCtrId = '', ProdUnitId  _prodUnitId = '', InventSiteId _inventSiteId = '')
{
    str qv = queryRange(JmgJobTableType::ProdQueueBefore, JmgJobTableType::ProdQueueAfter);
    qv = queryRangeConcat(qv, queryValue(JmgJobTableType::ProjProject));
    qv = queryRangeConcat(qv, queryValue(JmgJobTableType::ProjBudget));
    qv = queryRangeConcat(qv, queryValue(JmgJobTableType::ProjActivity));

    qbdsWrkCtrResourceAndGroupView.joinMode(JoinMode::InnerJoin);

    this.genQuery(qv, _wrkCtrId, _prodUnitId, _inventSiteId, curWorker);
}

void filterOnJobId()
{
    container   jobIds = controller.getJobIdsCon();
    str         queryVal = '';
    int i;
    boolean     first = true;
    for (i = 1;i <= conLen(jobIds);i++)
    {
        queryVal = first ? queryValue(conPeek(jobIds,i)) : queryRangeConcat(queryVal,queryValue(conPeek(jobIds,i)));
        first = false;
    }
    qbrProdJobType.value('');
    qbrJobId.value(queryVal);
}

void queryIPC()
{
    qbdsWrkCtrResourceAndGroupView.joinMode(JoinMode::OuterJoin);

    this.genQuery(
        queryRangeConcat(
            queryRangeConcat(
                queryRange(JmgJobTableType::SFCStartTeam,
                JmgJobTableType::SFCStopTeam),
                queryValue(JmgJobTableType::SFCActivity)), JmgJobTableType::SFCOnCall));
}

private void genQuery(str   _queryVal, str _wrkCtrId = '',str _prodUnitId = '',str _inventSiteId = '', JmgWorkerRecId _curWorker = 0)
{
    WrkCtrTable selectedWrkCtrTable;

    qbrProdJobType.value(_queryVal);

    this.querySetDefaultRanges();

    qbrIPCActivities.value(queryRange(JmgSignInOut::SignIn, JmgSignInOut::SwitchBundleMode));

    if (_wrkCtrId)
    {
        selectedWrkCtrTable = WrkCtrTable::find(_wrkCtrId);

        if (selectedWrkCtrTable.IsIndividualResource == NoYes::No)
        {
            qbrProdResourceGroup.value(queryValue(WrkCtrResourceGroup::findWrkCtrId(_wrkCtrId).RecId));
        }
        else
        {
            qbrProdWrkCtrId.value(_wrkCtrId);
        }
    }
    else if (_prodUnitId)
    {
        qbrProdProdUnit.value(_prodUnitId);
    }
    else if (_inventSiteId)
    {
        qbrProdInventSiteId.value(_inventSiteId);
    }

    if (!prmisDefault(_curWorker))
    {
        qbdsWrkCtrTableIndividualView.joinMode(JoinMode::InnerJoin);
        qbrWorker.value(queryValue(_curWorker));
    }

    jmgJobTable_DS.executeQuery();
}

public int active()
{
    int ret;

    ret = super();

    prevModule = module;

    if (jmgJobTable.Type == JmgJobTableType::ProdOverlap        ||
        jmgJobTable.Type == JmgJobTableType::ProdProcess        ||
        jmgJobTable.Type == JmgJobTableType::ProdQueueAfter     ||
        jmgJobTable.Type == JmgJobTableType::ProdQueueBefore    ||
        jmgJobTable.Type == JmgJobTableType::ProdSetup)
    {
        module = JmgModuleEnum::PROD;
    }
    else if (jmgJobTable.Type == JmgJobTableType::ProjActivity ||
        jmgJobTable.Type == JmgJobTableType::ProjBudget   ||
        jmgJobTable.Type == JmgJobTableType::ProjProject)
    {
        module = JmgModuleEnum::PROJ;
    }
    else
    {
        module = JmgModuleEnum::IPC;
    }

    if (module != prevModule)
    {
        prodGroup.visible(module == JmgModuleEnum::PROD);
        projGroup.visible(module == JmgModuleEnum::PROJ);
        ipcGroup.visible(module ==JmgModuleEnum::IPC);
        captionTitle = module == JmgModuleEnum::PROD ? "@SYS89639" : module == JmgModuleEnum::PROJ ? "@SYS39770" : "@SYS39775";
    }

    if (this.configurationContainsGrids())
    {
        previewTab.caption(strFmt("@SYS76498", captionTitle, jmgJobTable.ModuleRefId));

        startQty.enabled(jmgJobTable.canHaveStartQuantity());

        pdsCWStartQty.enabled(startQty.enabled() && PdsGlobal::pdsIsCWItem(jmgJobTable.ItemId));

        controller.pmfSelectedJobChanged(jmgJobTable);

        this.updateSelection();
    }

    return ret;
}

/// <summary>
/// The job card terminal can be configured to not show a grid
/// </summary>
/// <returns>If the job card terminal configuration contains at least one grid</returns>
private boolean configurationContainsGrids()
{
    return jmgRegistrationSetup.ipcGridSetupID
           || jmgRegistrationSetup.ProdGridSetupID
           || jmgRegistrationSetup.ProjGridSetupID;
}

public void init()
{
    super();

    qbdsJmgJobTable    = jmgJobTable_DS.query().dataSourceTable(tableNum(JmgJobTable));
    qbdsJmgIpcActivity = qbdsJmgJobTable.addDataSource(tableNum(JmgIpcActivity));
    qbdsJmgIpcActivity.joinMode(JoinMode::NoExistsJoin);
    qbdsJmgIpcActivity.addLink(fieldNum(JmgJobTable, JobId),fieldNum(JmgIpcActivity, JobId));

    if (JmgProdParameters::find().StampLevel == JmgStampLevel::Job)
    {
        qbdsProdRouteJob   = qbdsJmgJobTable.addDataSource(tableNum(ProdRouteJob));
        qbdsProdRouteJob.joinMode(JoinMode::NoExistsJoin);
        qbdsProdRouteJob.addLink(fieldNum(JmgJobTable, JobId),fieldNum(ProdRouteJob, JobId));
        qbdsProdRouteJob.addLink(fieldNum(JmgJobTable, OprNum),fieldNum(ProdRouteJob, OprNum));
        qbdsProdRouteJob.addLink(fieldNum(JmgJobTable, ModuleRefId),fieldNum(ProdRouteJob, ProdId));
        qbdsProdRouteJob.addLink(fieldNum(JmgJobTable, Priority),fieldNum(ProdRouteJob, OprPriority));

        qbdsProdRouteJob.addRange(fieldNum(ProdRouteJob, JobControl)).value(queryValue(NoYes::No));
    }

    qbrProdJobType          = qbdsJmgJobTable.addRange(fieldNum(JmgJobTable,Type));
    qbrProdWrkCtrId         = qbdsJmgJobTable.addRange(fieldNum(JmgJobTable,WrkCtrId));
