    qbrProdInventSiteId     = qbdsJmgJobTable.addRange(fieldNum(JmgJobTable,InventSiteId));
    qbrJobId                = qbdsJmgJobTable.addRange(fieldNum(JmgJobTable,JobId));

    qbrIPCActivities        = qbdsJmgIpcActivity.addRange(fieldNum(JmgIpcActivity, SignInOut));

    // Only show jobs which are not reported as finished.
    qbdsJmgJobTable.addRange(fieldNum(JmgJobTable,ReportedFinished)).value(queryValue(NoYes::No));
    qbdsJmgJobTable.addSortField(fieldNum(JmgJobTable, PriorityResource), SortOrder::Ascending);
    qbdsJmgJobTable.addSortField(fieldNum(JmgJobTable, FromDate), SortOrder::Ascending);
    qbdsJmgJobTable.addSortField(fieldNum(JmgJobTable, FromTime), SortOrder::Ascending);

    jmgJobTable_DS.cacheAddMethod(tableMethodStr(JmgJobTable,itemName));
    jmgJobTable_DS.cacheAddMethod(tableMethodStr(JmgJobTable,startedQuantity));
    jmgJobTable_DS.cacheAddMethod(tableMethodStr(JmgJobTable,jobStatus));
    jmgJobTable_DS.cacheAddMethod(tableMethodStr(JmgJobTable,badQuantity));
    jmgJobTable_DS.cacheAddMethod(tableMethodStr(JmgJobTable,goodQuantity));
    jmgJobTable_DS.cacheAddMethod(tableMethodStr(JmgJobTable,previousOperation));
    jmgJobTable_DS.cacheAddMethod(tableMethodStr(JmgJobTable,nextOperation));
    jmgJobTable_DS.cacheAddMethod(tableMethodStr(JmgJobTable,prevJobStatus));
    jmgJobTable_DS.cacheAddMethod(tableMethodStr(JmgJobTable,prevOperationQtyGood));
    jmgJobTable_DS.cacheAddMethod(tableMethodStr(JmgJobTable,projectStage));
    jmgJobTable_DS.cacheAddMethod(tableMethodStr(JmgJobTable,projStartDate));
    jmgJobTable_DS.cacheAddMethod(tableMethodStr(JmgJobTable,imageModule));
    jmgJobTable_DS.cacheAddMethod(tableMethodStr(JmgJobTable,showProjSiblings));
    jmgJobTable_DS.cacheAddMethod(tableMethodStr(JmgJobTable,showProjSubProjs));
    jmgJobTable_DS.cacheAddMethod(tableMethodStr(JmgJobTable,showProjParent));

    jmgJobTable_DS.cacheAddMethod(tableMethodStr(JmgJobTable,pdsCWStartedQuantity));
}

        public edit ProductQuantity startQty(boolean _set, JmgJobTable _jmgJobTable, ProductQuantity _value)

        {
            if (_set && _value > 0.0)
            {
                controller.jobMarked(true, _jmgJobTable, NoYes::Yes, false);
