private void updateSelection()
{
    boolean  recordsMarked = true;
    JmgJobTable selectedJob = JmgJobTable_DS.getFirst(true);
    if (!selectedJob)
    {
        recordsMarked = false;
        // - This will not mark any job as recordMarked is false and active job is passed. It will only check for enable/disable buttons.
        this.markSelectedJob(JmgJobTable_DS.getFirst(), recordsMarked);
    }

    Set selectedIds = new Set(Types::String);
    JmgJobTable oldSelection = controller.selectedJobs();

    while (selectedJob)
    {
        this.markSelectedJob(selectedJob, recordsMarked);
        selectedIds.add(selectedJob.JobId);
        selectedJob = JmgJobTable_DS.getNext();
    }

    while select oldSelection
    {
        this.markOldSelectedJob(oldSelection, selectedIds);
    }
}

/// <summary>
/// Marks the selected job.
/// If the selected set contains the jobId due to continuation of the bundle but the user has not actively
/// marked any records yet then only the toggling of the buttons should happen (no marking change).
/// </summary>
/// <param name = "_selectedJob">The <c>JmgJobTable</c> record to mark.</param>
/// <param name = "_markRecords">A boolean value specifying if the job should be marked.</param>
protected void markSelectedJob(JmgJobTable _selectedJob, boolean _markRecords)
{
    var jobIdSet = controller.getSelectedJobIdsSet();

    if (!_markRecords || !jobIdSet.in(_selectedJob.JobId))
    {
        controller.jobMarked(_markRecords, _selectedJob, NoYes::Yes, false);

        if (_markRecords)
        {
            controller.startJobQuantity(_selectedJob, startQty.realValue(), pdsCWStartQty.realValue());
        }
    }
}

/// <summary>
/// Removes the old selected jobs from bundle if they are no longer selected.
/// </summary>
/// <param name = "_oldSelectedJob">The <c>JmgJobTable</c> record to mark.</param>
/// <param name = "_selectedIds">A set with the selected job ids.</param>
protected void markOldSelectedJob(JmgJobTable _oldSelectedJob, Set _selectedIds)
{
    if (!_selectedIds.in(_oldSelectedJob.JobId))
    {
        controller.jobMarked(true, _oldSelectedJob, NoYes::No, false);
    }
}

