public void init()
{
    super();

    qbdsWrkCtrTableIndividualView = this.queryBuildDataSource();

    qbdsWrkCtrTableIndividualView.addLink(fieldNum(JmgJobTable, WrkCtrId), fieldNum(WrkCtrTableIndividualView, WrkCtrId));

    qbdsWrkCtrTableIndividualView.addRange(fieldNum(WrkCtrTableIndividualView,ValidFrom)).value(SysQuery::range(null, DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone())));
    qbdsWrkCtrTableIndividualView.addRange(fieldNum(WrkCtrTableIndividualView,ValidTo)).value(SysQuery::range(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()), null));

    qbrWorker = qbdsWrkCtrTableIndividualView.addRange(fieldNum(WrkCtrTableIndividualView, Worker));
}

void clicked()
{
    super();

    Args args = new Args();
    args.name(formstr(JmgLockScreenDialog));
    Object formRun = classfactory.formRunClass(args);
    formRun.parmCallerName(formStr(JmgRegistration));
    formRun.parmCurrentTimerValue(jmgRegistrationSetup.LockScreenSeconds);
    formRun.init();
    formRun.run();
    formRun.detach();
    WorkerTextField.setFocus();
}
