[Form]
public class JmgResourceJobs extends FormRun
{
    JmgResourceJobsForm resourceJobsForm;
    FormGroupControl    activeEmployeesGroup;

    QueryBuildRange         qbrProdWrkCtrId;
    QueryBuildRange         qbrProdResourceGroup;
    QueryBuildRange         qbrProdProdUnit;
    JmgMESDocuHandling      docHand;

}

public JmgJobPriority getMoveFromLine()
{
    return resourceJobsForm.getMinimumSelectedPriority();
}

public WrkCtrId getSelectedResource()
{
    return resourceJobsForm.parmResource();
}

public void init()
{
    resourceJobsForm    = new JmgResourceJobsForm();

    super();

    docHand = JmgMESDocuHandling::construct(jmgMESShowAttachments);

    resourceJobsForm.setFormDataSource(jmgJobTable_DS);

    this.setCurrentResource();

    resourceJobsForm.registerButtons(moveUpButton,moveDownButton,moveToLineButton,moveToResourceButton,lockSequenceButton,unlockSequenceButton);
}

public void moveToResource(WrkCtrId _moveToResource)
{
    if (_moveToResource)
    {
        resourceJobsForm.moveToResource(_moveToResource);
    }
}

public boolean promptForResource()
{
    Args        args;
    FormRun     requestActionDialog;
    Object      formRunObject;

    args = new Args(formStr(JmgMESRequestActionDialog));

    args.caller(element);

    args.parm(resourceJobsForm.parmResource() ? resourceJobsForm.parmResource() : resourceJobsForm.parmCurrentProdUnit());

    requestActionDialog = classfactory.formRunClass(args);
    requestActionDialog.run();
    requestActionDialog.wait(true);

    formRunObject = requestActionDialog;

    if (!formRunObject.isDialogCancelled())
    {
        resourceJobsForm.parmResource(formRunObject.selectedResource(false, '') ? formRunObject.selectedResource(false, '') : formRunObject.selectedResourceGroup(false, ''));
        resourceJobsForm.parmCurrentProdUnit(formRunObject.selectedProdUnitId(false, ''));

        this.updateTitle();

        return true;
    }

    return false;
}

public void run()
{
    element.args().initialQuery(null);
    element.args().lookupRecord(null);

    super();

    // Check to see if this is a Move job operation
    if (element.args().parmObject()                                 &&
        classIdGet(element.args().parmObject()) == classNum(Map))
    {
        resourceJobsForm.addJobsToResource(element.args().parmObject());
    }
}

private void setCurrentResource()
{
    FormRun caller;
    Object  callerObject;

    // Get from Args
    if (element.args() && element.args().parm())
    {
        resourceJobsForm.parmResource(element.args().parm());
    }

    // If not set, fetch from caller
    if (!resourceJobsForm.parmResource() && element.args().caller())
    {
        caller          = element.args().caller();
        callerObject    = caller;

        switch (caller.name())
        {
            case formStr(JmgMESRequestActionDialog):
                resourceJobsForm.parmResource(callerObject.selectedResource(false, '') ? callerObject.selectedResource(false, '') : callerObject.selectedResourceGroup(false, ''));
                resourceJobsForm.parmCurrentProdUnit(callerObject.selectedProdUnitId(false, ''));
                break;

            case formStr(JmgShopSupervisorMyTeamJobs):
                JmgShopSupervisorWorkspaceConfiguration config = JmgShopSupervisorWorkspaceConfiguration::findOrCreateForCurrentUser();

                resourceJobsForm.parmResource(config.ResourceFilter ? config.ResourceFilter : config.ResourceGroupFilter);
                resourceJobsForm.parmCurrentProdUnit(config.ProdUnitFilter);
                break;
        }
    }

    // If not set, prompt user:
    if (!resourceJobsForm.parmResource())
    {
        if (!this.promptForResource())
        {
            element.close();
        }
    }

    if (resourceJobsForm.parmResource())
    {
        this.updateTitle();
    }
}

private void updateTitle()
{
    element.design().caption(strFmt("%1 - %2 - %3", "@SYS304386", WrkCtrTable::find(resourceJobsForm.parmResource()).Name, JmgJobTable.JobId));
}

public boolean canClose()
{
    boolean ret;

    ret = super();

    if (ret && resourceJobsForm.parmIsPendingSave())
    {
        ret = Box::confirm("@SCM:JmgResourceJobsUnsavedChangesConfirmation");
    }

    return ret;
}

public void executeQuery()
{
    WrkCtrTable selectedWrkCtrTable;

    if (resourceJobsForm.parmResource())
    {
        selectedWrkCtrTable = WrkCtrTable::find(resourceJobsForm.parmResource());

        if (selectedWrkCtrTable.IsIndividualResource == NoYes::No)
        {
            qbrProdResourceGroup.value(queryValue(WrkCtrResourceGroup::findWrkCtrId(resourceJobsForm.parmResource()).RecId));
        }
        else
        {
            qbrProdWrkCtrId.value(resourceJobsForm.parmResource());
        }
    }
    else if (resourceJobsForm.parmCurrentProdUnit())
    {
        qbrProdProdUnit.value(resourceJobsForm.parmCurrentProdUnit());
    }

    super();
}

public int active()
{
    int ret;

    ret = super();

    resourceJobsForm.dataSourceActive(jmgJobTable);

    sequenceButtonGroup.enabled(true);

    element.updateTitle();

    return ret;
}

public void init()
{
    QueryBuildDataSource    qbdsJmgJobTable;

    super();

    qbdsJmgJobTable = this.queryBuildDataSource();

    jmgJobTable_DS.cacheAddMethod(tablemethodstr(JmgJobTable, pdsCWGoodQuantity));
    jmgJobTable_DS.cacheAddMethod(tablemethodstr(JmgJobTable, pdsCWBadQuantity));
    qbrProdWrkCtrId         = qbdsJmgJobTable.addRange(fieldNum(JmgJobTable, WrkCtrId));
    qbrProdResourceGroup    = qbdsJmgJobTable.addRange(fieldNum(JmgJobTable, ResourceGroup));

    qbdsJmgJobTable.addOrderByField(fieldnum(JmgJobTable, PriorityResource));

    jmgJobTable_DS.cacheAddMethod(tableMethodStr(JmgJobTable, goodQuantity));
    jmgJobTable_DS.cacheAddMethod(tableMethodStr(JmgJobTable, badQuantity));
}

public display container lockedWindow(JmgJobTable _jmgJobTable)
{
    container imgContainer = connull();

    if (_jmgJobTable.Locked)
    {
        ImageReference imgRef = ImageReference::constructForSymbol(ImageReferenceSymbol::HighPriority);
        imgContainer = imgRef.pack();
    }

    return imgContainer;
}

public edit NoYes selected(boolean _set, JmgJobTable _jmgJobTable, NoYes _value)
{
    NoYes res = resourceJobsForm.selectJob(_set, _jmgJobTable, _value);
    docHand.updateDocsForAll(resourceJobsForm.getSelectedJobIds());

    return res;
}

public void init()
{
    QueryBuildDataSource    qbdsWrkCtrResourceAndGroupView;

    super();

    qbdsWrkCtrResourceAndGroupView = this.queryBuildDataSource();

    JmgWrkCtrResourceAndGroupViewHelper::addLinkToJmgJobTable(qbdsWrkCtrResourceAndGroupView);

    qbrProdProdUnit = qbdsWrkCtrResourceAndGroupView.addRange(fieldNum(WrkCtrResourceAndGroupView, ProdUnitId));
}

