public void dialogClosed(xFormRun _formRun)
{
    Object moveToLine;

    super(_formRun);

    if (_formRun.name() == formStr(JmgResourceJobsMoveToLineDialog))
    {
        moveToLine = _formRun;
        resourceJobsForm.moveToLine(moveToLine.getMoveToLine());
    }
}

