[Form]
public class JmgShopSupervisorReleaseOrders extends FormRun implements SysIFilterConsumerForm
{
    private InventDimCtrl_Frm prodOrderInventDimCtrl;
    private InventDimCtrl_Frm pickingListInventDimCtrl;

    private JmgShopSupervisorReleaseOrdersPickingListProvider pickingListProvider;
    private Set     prodIdsOnForm;
    private Set     prodIdsSelected;

}

public void registerDatasourceOnQueryingEvent()
{
    ProdSupervisorScheduledOrders_DS.OnQueryExecuting += eventhandler(this.parmFilterFormQueryEventHandler().applyFilter);
}

public void init()
{
    prodIdsOnForm   = new Set(Types::String);
    prodIdsSelected = new Set(Types::String);

    ReqParameters::checkPlanningProcessesAreEnabled();

    super();

    element.updateDesign(InventDimFormDesignUpdate::Init);

    if (!pickingListProvider)
    {
        ProdTableTmpFilter prodIds; // do not do explosion
        pickingListProvider = JmgShopSupervisorReleaseOrdersPickingListProvider::construct(prodIds);
    }

    ProdSupervisorScheduledOrders_LastMaterialAvailCheckDate.visible(true);

    element.dataHelper().Refreshing += eventhandler(this.onRefresh);
}

public void updateControls()
{
    if (this.selectedControl() && this.selectedControl().name() == PickingListDisplayInventDim.name())
    {
        JmgShopSupervisorTmpPickingList_DS.executeQuery();
    }
}

public void updateDesign(InventDimFormDesignUpdate _mode)
{
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            prodOrderInventDimCtrl = InventDimCtrl::newFromFormValidDataSource(element, InventDimProdOrders_DS.name());
            pickingListInventDimCtrl = InventDimCtrl::newFromFormValidDataSource(element, InventDimPickingList_DS.name());
        // No break
        case InventDimFormDesignUpdate::LinkActive:
            prodOrderInventDimCtrl.parmDimParmVisible(prodOrderInventDimCtrl.parmDimParmVisibleGrid());
            prodOrderInventDimCtrl.formActiveSetup();
            prodOrderInventDimCtrl.formSetControls(true, true);

            pickingListInventDimCtrl.parmDimParmVisible(pickingListInventDimCtrl.parmDimParmVisibleGrid());
            pickingListInventDimCtrl.formActiveSetup();
            pickingListInventDimCtrl.formSetControls(true, true);
            break;
        default:
            throw error(strfmt("@SYS54195",funcname()));
    }
}

/// <summary>
///    Returns to the Dimensions display framework the inventDimSetupObject that should be used depending
///    on which Dimensions display button was selected (for quality orders or for nonconformances).
/// </summary>
/// <returns>Inventory dimension setup</returns>
public Object inventDimSetupObject()
{
    var sel = this.selectedControl();

    if (this.selectedControl().name() == ProductionOrdersDisplayInventDim.name())
    {
        return prodOrderInventDimCtrl;
    }
    if (this.selectedControl().name() == PickingListDisplayInventDim.name())
    {
        return pickingListInventDimCtrl;
    }

    return null;
}

[Hookable(false)]
internal Set parmProdIdsSelected(Set _prodIdsSelected = prodIdsSelected)
{
    prodIdsSelected = _prodIdsSelected;
    return prodIdsSelected;  
}

/// <summary>
/// Perform a full refresh on the <c>ProdSupervisorScheduledOrders</c> datasource after executing material availability check.
/// </summary>
[Hookable(false)]
internal void refreshMaterialStatus()
{
    ProdSupervisorScheduledOrders_ds.reread();
    ProdSupervisorScheduledOrders_DS.rereadReferenceDataSources();
    ProdSupervisorScheduledOrders_DS.research(true);
}

/// <summary>
/// Update critical on-hand inventory on refresh.
/// </summary>
/// <param name = "_cancelEventsArgs"> The <c>FormRunServiceArgs</c> record </param>
[Hookable(false)]
internal void onRefresh(FormRunServiceArgs _cancelEventsArgs)
{
    JmgShopSupervisorTmpPickingList_DS.executeQuery();
}

/// <summary>
/// Updates the critical on-hand inventory for a set of production orders.
/// </summary>
/// <param name = "_prodIds"> Temporary buffer containing selected production orders </param>
private void updateCriticalOnHandInventory(ProdTableTmpFilter _prodIds)
{
    Set prevProdIdsSelected = prodIdsSelected;

    boolean materialChecked = true;

    while select _prodIds
    {
        if (!JmgMaterialAvailabilityCheckOrder::findByProdId(_prodIds.ProdId))
        {
            materialChecked = false;
            break;
        }
    }

    if (materialChecked)
    {
        pickingListProvider = JmgShopSupervisorReleaseOrdersPickingListProvider::construct(null);
        var pickingList = pickingListProvider.aggregate(_prodIds);
        JmgShopSupervisorTmpPickingList.setTmpData(pickingList);
    }
    else
    {
        JmgShopSupervisorTmpPickingList.setTmpData(null);
    }
}

private ProdTableTmpFilter getProductionOrderIds(boolean _onlySelected = true)
{
    ProdTableTmpFilter prodOrderIds;
    Set prodIds = new Set(Types::String);

    var ds = ProdSupervisorScheduledOrders_DS;
    ProdSupervisorScheduledOrders prodOrder = ds.getFirst(_onlySelected) ? ds.getFirst(_onlySelected) : ds.cursor();

    while (prodOrder)
    {
        prodOrderIds.ProdId = prodOrder.ProdId;
        prodOrderIds.insert();

        prodIds.add(prodOrderIds.ProdId);

        prodOrder = ds.getNext();
    }

    if (_onlySelected)
    {
        prodIdsSelected = prodIds;
    }
    else
    {
        prodIdsOnForm = prodIds;
    }

    return prodOrderIds;
}

private boolean markAllProductionOrdersAvailableForRelease()
{
    var ds = ProdSupervisorScheduledOrders_DS;
    boolean anyProductionOrderToRelease;
    ProdSupervisorScheduledOrders prodOrder = ds.getFirst();

    while (prodOrder)
    {
        JmgShopSupervisorMaterialAvailabilityStatus status;
        if (prodOrder.MaterialStatus)
        {
            status = prodOrder.MaterialStatus;
        }
        else
        {
            status = pickingListProvider.getMaterialAvailabilityForProdOrder(prodOrder.ProdId);

            JmgMaterialAvailabilityCheckOrder jmgMaterialAvailabilityCheckOrder = JmgMaterialAvailabilityCheckOrder::findByProdId(prodOrder.ProdId, true);

            jmgMaterialAvailabilityCheckOrder.LastUpdated = DateTimeUtil::newDateTime(JmgProfileSeconds::getSysDate(), JmgProfileSeconds::getSysTime(),DateTimeUtil::getUserPreferredTimeZone());;
            jmgMaterialAvailabilityCheckOrder.MaterialStatus = status;

            if (jmgMaterialAvailabilityCheckOrder)
            {
                jmgMaterialAvailabilityCheckOrder.update();
            }
            else
            {
                jmgMaterialAvailabilityCheckOrder.ProdId = prodOrder.ProdId;
                jmgMaterialAvailabilityCheckOrder.insert();
            }
        }

        ds.markRecord(prodOrder, status == JmgShopSupervisorMaterialAvailabilityStatus::Available);

        if (!anyProductionOrderToRelease)
        {
            anyProductionOrderToRelease = status == JmgShopSupervisorMaterialAvailabilityStatus::Available;
        }

        prodOrder = ds.getNext();
    }
    return anyProductionOrderToRelease;
}

