/// <summary>
/// Displays whether the production order has materials available and is ready to be released.
/// </summary>
/// <param name = "_prodOrder"> The current production order record.</param>
/// <returns>
/// A resource identifier of the icon to show.
/// </returns>
public display container materialAvailabilityStatusImg(ProdSupervisorScheduledOrders _prodOrder)
{
    if (!_prodOrder.MaterialStatus)
    {
        return ImageReference::constructForSymbol(ImageReferenceSymbol::Help).pack();
    }

    JmgShopSupervisorMaterialAvailabilityStatus status = _prodOrder.MaterialStatus;

    str imgSymbol;
    switch (status)
    {
        case JmgShopSupervisorMaterialAvailabilityStatus::Unknown:
            imgSymbol = ImageReferenceSymbol::Help;
            break;
        case JmgShopSupervisorMaterialAvailabilityStatus::NotAvailable:
            imgSymbol = ImageReferenceSymbol::NotAvailable;
            break;
        case JmgShopSupervisorMaterialAvailabilityStatus::Upcoming:
            imgSymbol = ImageReferenceSymbol::Warning;
            break;
        case JmgShopSupervisorMaterialAvailabilityStatus::Available:
            imgSymbol = ImageReferenceSymbol::Accept;
            break;
    }

    return ImageReference::constructForSymbol(imgSymbol).pack();
}

public void executeQuery()
{
    QueryBuildDataSource prodOrdersDS = this.queryBuildDataSource();

    str queryRange;
    if (JmgMaterialAvailabilityCheckOpSchedFeature::instance().isEnabled())
    {
        queryRange = queryRange(ProdSchedStatus::OperationScheduled, ProdSchedStatus::JobScheduled);
    }
    else
    {
        queryRange = queryValue(ProdSchedStatus::JobScheduled);
    }

    SysQuery::findOrCreateRange(prodOrdersDS, fieldNum(ProdSupervisorScheduledOrders, SchedStatus)).value(queryRange);
    super();
}

public void executeQuery()
{
    if (PickingListTab.isActivePage())
    {
        ProdTableTmpFilter prodIds = element.getProductionOrderIds();
        element.updateCriticalOnHandInventory(prodIds);
    }

    super();
}

public void init()
{
    super();

    if (JmgMaterialAvailabilityCheckOpSchedFeature::instance().isEnabled())
    {
        QueryBuildDataSource prodRouteJobDatasource = this.query().dataSourceTable(tableNum(ProdRouteJob));
        prodRouteJobDatasource.enabled(false);
    }
}

