public void clicked()
{
    super();

    if (element.markAllProductionOrdersAvailableForRelease())
    {
        ReleaseButton.clicked();
    }
    else
    {
        info("@SCM:JmgShopSupervisorReleaseAllAvailableOrders");
    }
}

public void clicked()
{
    ProdTable prodTable;
    ProdTableTmpFilter prodOrderIds = element.getProductionOrderIds();
    select firstOnly prodOrderIds join prodTable where prodTable.ProdId == prodOrderIds.ProdId;

    var args = new Args();
    args.record(prodTable);
    args.caller(element);
    new MenuFunction(menuItemActionStr(ReqCalcExplode), MenuItemType::Action).run(args);

    // New material records may appear after running explosion, so perform a full refresh.
    ProdSupervisorScheduledOrders_DS.reread();
    ProdSupervisorScheduledOrders_DS.rereadReferenceDataSources();
    ProdSupervisorScheduledOrders_DS.research(true);
}

