public void jumpRef()
{
    if (InventLocation::find(JmgShopSupervisorTmpPickingList.InventLocationId).whsEnabled)
    {
        Args args = new Args();
        args.caller(element);
        args.record(WMSLocation::find(JmgShopSupervisorTmpPickingList.WMSLocationId, JmgShopSupervisorTmpPickingList.InventLocationId));
        args.refField(fieldNum(WMSLocation, WMSLocationId));

        new MenuFunction(MenuItemDisplayStr(WHSLocationFormLauncher), MenuItemType::Display).run(args);
    }
    else
    {
        super();
    }
}
