Method.Source:
    [Form]
    public class JmgStampTrans extends FormRun
    {
        QueryBuildRange                 workerRange;
        QueryBuildRange                 profileDateRange;
}

public void init()
{
    super();

    QueryBuildDataSource queryBuildDataSource = SysQuery::findOrCreateDataSource(jmgStampTrans_ds.query(), tableNum(JmgStampTrans));
    QueryBuildRange      jobIdRange           = SysQuery::findOrCreateRange(queryBuildDataSource, fieldnum(JmgStampTrans, JobId));

    profileDateRange = SysQuery::findOrCreateRange(queryBuildDataSource, fieldnum(JmgStampTrans, ProfileDate));
    workerRange      = SysQuery::findOrCreateRange(queryBuildDataSource, fieldnum(JmgStampTrans, Worker));

    if (element.args() && element.args().record())
    {
        Common rec = element.args().record();
        if (rec is JmgStampJournalTable)
        {
            JmgStampJournalTable jmgStampJournalTable = rec;
            chooseWorker.value(jmgStampJournalTable.Worker);
            chooseWorker.allowEdit(false);
            chooseProfileDate.dateValue(jmgStampJournalTable.ProfileDate);
        }
        else if (rec is JmgTransferredTimeAndAttendanceActivityRegistrationTimeStaging)
        {
            JmgTransferredTimeAndAttendanceActivityRegistrationTimeStaging jmgTransferredTimeAndAttendanceActivityRegistrationTimeStaging = rec;
            chooseWorker.value(HCMWorker::findByPersonnelNumber(jmgTransferredTimeAndAttendanceActivityRegistrationTimeStaging.WorkerPersonnelNumber).RecId);
            chooseWorker.allowEdit(false);
            chooseProfileDate.dateValue(jmgTransferredTimeAndAttendanceActivityRegistrationTimeStaging.TimeProfileDate);
        }
        else if (rec is JmgIpcLedgerJournal)
        {
            JmgIpcLedgerJournal jmgIpcLedgerJournal = rec;
            // It is only relevant to show indirect activity group.
            productionInfo.visible(false);
            projectInfo.visible(false);
        }
        else if (rec is JmgJobTable)
        {
            JmgJobTable jmgJobTable = rec;
            jobIdRange.value(queryValue(jmgJobTable.JobId));
        }
    }
}

void run()
{
    str formCaller;
    if (element.args() && element.args().caller())
    {
        formCaller = element.args().caller().name();
    }

    JmgStampTrans::imageListModule(imageModule);
    JmgStampTrans::imageListJobType(imageJobType);

    super();

    if (formCaller == 'JmgProdStatusListPagePayrollCostDetails'
     || formCaller == 'jmgProdStatusListPagePayrollCosts'
     || formCaller == 'JmgPayrollCostDetailsPart'
     || formCaller == 'JmgPayrollCostPart')
    {
        element.selectControl(costPrice);
    }
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        ShowErrorCausesBtn.enabled(JmgStampTrans.hasJobRegistrationError());
    }

    return ret;
}

public void executeQuery()
{
    JmgWorkerRecId  worker = chooseWorker.value();

    workerRange.value(worker ? queryValue(worker) : SysQuery::valueUnlimited());
    if (chooseProfileDate.dateValue())
    {
        profileDateRange.value(queryRange(chooseProfileDate.dateValue(), chooseProfileDate.dateValue()));
    }
    else
    {
        profileDateRange.value("");
    }

    super();
}

public boolean modified()
{
    boolean ret;

    ret = super();

    element.refreshGrid();

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    element.refreshGrid();

    return ret;
}

void clicked()
{
    JmgErrorSpecificationForm errorSpecificationForm;

    super();
    errorSpecificationForm = new JmgErrorSpecificationForm(jmgStampTrans, element, false);
    errorSpecificationForm.showErrorSpecificationForm();
}
