[Form]
public class JmgTimecard extends FormRun implements OfficeIGeneratedWorkbookCustomExporter, OfficeIMenuCustomizer
{
    const str ExportLinesToExcelOptionId = 'LinesCustom';

    [FormObservable]
    JmgWorkerRecId      currentWorker;
    JmgProfileDate      currentProfileDate;
    JmgWeekNumberTmp    weekNumberTmp;
    Week                currentWeekNumber;

    QueryBuildRange     workerRangeTable;
    QueryBuildRange     profileDateRangeTable;

    QueryBuildRange     profileRangeProfileSpec;
    QueryBuildRange     dayIdRangeProfileSpec;

    [FormObservable]
    JmgFromTime         profileStartTime;
    [FormObservable]
    JmgToTime           profileEndTime;
    [FormObservable]
    JmgSeconds          profileSeconds;

}

public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    var exportOption = OfficeGeneratedExportMenuItem::construct(tableStr(JmgElectronicTimecardActivityRegistrationLineEntity), ExportLinesToExcelOptionId);
    exportOption.displayName("@SCM:ElectronicTimecardHeaderAndLines");
    _menuOptions.customMenuItems().addEnd(exportOption);
}

    public ExportToExcelDataEntityContext getDataEntityContext(OfficeGeneratedExportMenuItem _menuItem)
    {
        ExportToExcelDataEntityContext context = null;

        if (_menuItem.id() == ExportLinesToExcelOptionId)
        {
            var headerEntityName = tableStr(JmgElectronicTimecardActivityRegistrationHeaderEntity);
			var lineEntityName = tableStr(JmgElectronicTimecardActivityRegistrationLineEntity);
            var currentWorkerPersonnelNumber = HcmWorker::find(currentWorker).PersonnelNumber;

            context = ExportToExcelDataEntityContext::constructDefault(headerEntityName);

            var headerEntityInfo = context.findEntityByName(headerEntityName); 
            var headerFilterBuilder = new ExportToExcelFilterTreeBuilder(headerEntityName);
            var headerFilter = headerFilterBuilder.and(
					headerFilterBuilder.companyFilter(),
					headerFilterBuilder.and(
                        headerFilterBuilder.areEqual(fieldStr(JmgElectronicTimecardActivityRegistrationHeaderEntity, WorkerPersonnelNumber), currentWorkerPersonnelNumber),
						headerFilterBuilder.areEqual(fieldStr(JmgElectronicTimecardActivityRegistrationHeaderEntity, TimeProfileDate), currentProfileDate)
					)
				);
            headerEntityInfo.filter(headerFilter);

            var lineEntityInfo = context.addEntityDefault(lineEntityName);
            var lineFilterBuilder = new ExportToExcelFilterTreeBuilder(lineEntityName);
            var lineFilter = lineFilterBuilder.and(
					lineFilterBuilder.companyFilter(),
                    lineFilterBuilder.and(
                        lineFilterBuilder.areEqual(fieldStr(JmgElectronicTimecardActivityRegistrationLineEntity, WorkerPersonnelNumber), currentWorkerPersonnelNumber),
                        lineFilterBuilder.areEqual(fieldStr(JmgElectronicTimecardActivityRegistrationLineEntity, TimeProfileDate), currentProfileDate)
                    )
                );
            lineEntityInfo.filter(lineFilter);
        }

        return context;
    }

public edit JmgProfileDate currentProfileDate(boolean _set, JmgProfileDate _value)
{
    if (_set)
    {
        currentProfileDate = _value;
        this.synchronizeProfileDate();
    }
    return currentProfileDate;
}

public edit Week currentWeek(boolean _set, Week _value)
{
    int changeInWeeks;
    if (_set)
    {
        changeInWeeks = _value - currentWeekNumber;
        currentWeekNumber = _value;
        this.synchronizeWeekNumber(changeInWeeks);
    }
    return currentWeekNumber;
}

public display JmgWorkerRecId currentWorker()
{
    return currentWorker;
}

public void enableTimecardTrans(JmgTimecardTable _jmgTimecardTable)
{
    if (_jmgTimecardTable.RegistrationsTransferred  == NoYes::Yes ||
        _jmgTimecardTable.dateCalculated()          == NoYes::Yes ||
        _jmgTimecardTable.dateLocked()              == NoYes::No)
    {
        jmgTimecardTrans_DS.allowEdit(false);
        jmgTimecardTrans_DS.allowCreate(false);
        jmgTimecardTrans_DS.allowDelete(false);
    }
    else
    {
        jmgTimecardTrans_DS.allowEdit(true);
        jmgTimecardTrans_DS.allowCreate(true);
        jmgTimecardTrans_DS.allowDelete(true);
    }
}

    void init()
    {
        super();

        this.initRanges();

        if (!this.initValuesFromCaller())
        {
            currentProfileDate = DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone());
			this.initWorker();
        }

		currentWeekNumber = wkofyr(currentProfileDate);

        this.setShowStartDate();
        this.setShowStopDate();

        weekDay.selection(dayofwk(currentProfileDate) - 1);

        this.workerProfileDateChanged();
    }

	/// <summary>
    /// Initializes values from the caller record.
    /// </summary>
    /// <returns>
    /// true if the values were initialized from the caller record; otherwise, false.
	/// </returns>
	private boolean initValuesFromCaller()
    {
        var args = element.args();

        if (!args)
        {
			return false;
        }

        var argsRecord = args.record();
        var argsDataset = args.dataset();

        if (argsRecord)
        {
			if (argsDataset == tableNum(JmgElectronicTimecardActivityRegistrationHeaderStaging))
			{
				JmgElectronicTimecardActivityRegistrationHeaderStaging callerRecord = argsRecord;
				currentProfileDate = callerRecord.TimeProfileDate;
				this.initWorkerFromWorkerPersonnelNumber(callerRecord.WorkerPersonnelNumber);
				return true;
			}

			if (argsDataset == tableNum(JmgElectronicTimecardActivityRegistrationLineStaging))
			{
				JmgElectronicTimecardActivityRegistrationLineStaging callerRecord = argsRecord;
				currentProfileDate = callerRecord.TimeProfileDate;
				this.initWorkerFromWorkerPersonnelNumber(callerRecord.WorkerPersonnelNumber);
				return true;
			}
        }

        return false;
    }

private void initRanges()
{
    workerRangeTable        = jmgTimecardTable_DS.query().dataSourceTable(tablenum(JmgTimecardTable)).addRange(fieldnum(JmgTimecardTable,Worker));
    profileDateRangeTable   = jmgTimecardTable_DS.query().dataSourceTable(tablenum(JmgTimecardTable)).addRange(fieldnum(JmgTimecardTable,ProfileDate));

    profileRangeProfileSpec = jmgProfileSpec_DS.query().dataSourceTable(tablenum(JmgProfileSpec)).addRange(fieldnum(JmgProfileSpec,Profile));
    dayIdRangeProfileSpec   = jmgProfileSpec_DS.query().dataSourceTable(tablenum(JmgProfileSpec)).addRange(fieldnum(JmgProfileSpec,DayId));

    jmgTimecardTrans_DS.query().dataSourceTable(tablenum(JmgTimecardTrans)).addSortField(fieldnum(JmgTimecardTrans, StartDateTime), SortOrder::Ascending);
    jmgTimecardTrans_DS.query().dataSourceTable(tablenum(JmgTimecardTrans)).addSortField(fieldnum(JmgTimecardTrans, StopDateTime), SortOrder::Ascending);
}

private void initWorker()
{
    MenuFunction    mf;
    Args            args;
    args = new Args(this);
    this.args().parm(date2str(currentProfileDate, 123, 2, 2, 2, 2, 2, DateFlags::None));
    mf = new MenuFunction(menuitemactionstr(JmgTimecardChangeEmployee), MenuItemType::Action);
    mf.run(args);

    if (this.args().parmEnum() == NoYes::No)
    {
        this.close();
    }
    else
    {
        this.synchronizeWorker(str2int64(this.args().parm()));
    }
}

private void initWorkerFromWorkerPersonnelNumber(HcmPersonnelNumberId _personnelNumber)
{
    this.synchronizeWorker(HcmWorker::findByPersonnelNumber(_personnelNumber).RecId);
}

void openClosePropertyFields(JmgTimecardTrans _jmgTimecardTrans)
{
    jmgTimecardTrans_JobIDAbs.allowEdit(false);
    jmgTimecardTrans_WrkCtrId.allowEdit(false);
    jmgTimecardTrans_ProjCategory.allowEdit(false);
    jmgTimecardTrans_ProjCategory_grid.allowEdit(false);
    jmgTimecardTrans_OnCallActivity.allowEdit(false);

    jmgTimecardTrans_JobIDAbs.skip(true);
    jmgTimecardTrans_WrkCtrId.skip(true);
    jmgTimecardTrans_ProjCategory.skip(true);
    jmgTimecardTrans_OnCallActivity.skip(true);
    jmgTimecardTrans_ProjCategory_grid.skip(true);

    switch (_jmgTimecardTrans.JourRegType)
    {
        case JmgJourRegTypeEnum::MrpOverlap:
        case JmgJourRegTypeEnum::MrpProcess:
        case JmgJourRegTypeEnum::MrpQueueAfter:
        case JmgJourRegTypeEnum::MrpQueueBefore:
        case JmgJourRegTypeEnum::MrpSetup:
        case JmgJourRegTypeEnum::MrpTransport:
            jmgTimecardTrans_WrkCtrId.allowEdit(true);
            jmgTimecardTrans_WrkCtrId.skip(false);
            break;
        case JmgJourRegTypeEnum::PrjActivity:
        case JmgJourRegTypeEnum::PrjProject:
            jmgTimecardTrans_ProjCategory.allowEdit(true);
            jmgTimecardTrans_ProjCategory.skip(false);
            jmgTimecardTrans_ProjCategory_grid.allowEdit(true);
            jmgTimecardTrans_ProjCategory_grid.skip(false);
            break;
        case JmgJourRegTypeEnum::SignIn:
            jmgTimecardTrans_OnCallActivity.allowEdit(true);
            jmgTimecardTrans_OnCallActivity.skip(false);
        case JmgJourRegTypeEnum::SignOut:
            jmgTimecardTrans_JobIDAbs.allowEdit(true);
            jmgTimecardTrans_JobIDAbs.skip(false);
            break;
        case JmgJourRegTypeEnum::StartTeam:
        case JmgJourRegTypeEnum::StopTeam:
        case JmgJourRegTypeEnum::Absence:
        case JmgJourRegTypeEnum::BreakReg:
        case JmgJourRegTypeEnum::Empty:
        case JmgJourRegTypeEnum::IndActivity:
        default:
            break;
    }
}

public void recalculateProfileData()
{
    JmgProfiles jmgProfiles;
    JmgStampJournalTable stampJournalTable;
    jmgProfiles = new JmgProfiles(false);
    jmgProfiles.getProfile(jmgProfileSpec.Profile, jmgProfileSpec.DayId);
    jmgProfiles.initStampJournalTable(stampJournalTable);

    profileStartTime    = stampJournalTable.ProfileStart;
    profileEndTime      = stampJournalTable.ProfileEnd;
    profileSeconds      = stampJournalTable.ProfileSeconds;
}

private void refreshDatasources()
{
    jmgTimecardTable_DS.executeQuery();
}

public void setActiveButtons(JmgTimecardTable _jmgTimecardTable)
{
    if (_jmgTimecardTable.dateCalculated()              == NoYes::Yes)
    {
        jmgValidateTimecard.enabled(false);
        jmgTransferTimecard.enabled(false);
        jmgCancelTransferTimecard.enabled(false);
        lockUnlock.enabled(false);
        jmgEventCtrl.enabled(false);
    }
    else if (_jmgTimecardTable.RegistrationsTransferred == NoYes::Yes)
    {
        jmgValidateTimecard.enabled(false);
        jmgTransferTimecard.enabled(false);
        jmgCancelTransferTimecard.enabled(true);
        lockUnlock.enabled(false);
        jmgEventCtrl.enabled(false);
    }
    else if (_jmgTimecardTable.dateLocked()             == NoYes::Yes)
    {
        jmgValidateTimecard.enabled(true);
        jmgTransferTimecard.enabled(true);
        jmgCancelTransferTimecard.enabled(false);
        lockUnlock.enabled(true);
        jmgEventCtrl.enabled(true);
    }
    else
    {
        jmgValidateTimecard.enabled(false);
        jmgTransferTimecard.enabled(false);
        jmgCancelTransferTimecard.enabled(false);
        lockUnlock.enabled(true);
        jmgEventCtrl.enabled(false);
    }
}

public void setInputMode(JmgTimecardTrans _jmgTimecardTrans)
{
    if (_jmgTimecardTrans.JourRegType   == JmgJourRegTypeEnum::SignIn ||
        _jmgTimecardTrans.JourRegType   == JmgJourRegTypeEnum::SignOut ||
        _jmgTimecardTrans.JourRegType   == JmgJourRegTypeEnum::Absence)
    {
        grid_regTime.skip(true);
        grid_regTime.allowEdit(false);

        grid_StartDateTime.skip(false);
        grid_StartDateTime.allowEdit(true);
        grid_StartTime.skip(false);
        grid_StartTime.allowEdit(true);

        grid_StopDateTime.skip(_jmgTimecardTrans.JourRegType != JmgJourRegTypeEnum::Absence);
        grid_StopDateTime.allowEdit(_jmgTimecardTrans.JourRegType == JmgJourRegTypeEnum::Absence);
        grid_StopTime.skip(_jmgTimecardTrans.JourRegType != JmgJourRegTypeEnum::Absence);
        grid_StopTime.allowEdit(_jmgTimecardTrans.JourRegType == JmgJourRegTypeEnum::Absence);
    }
    else
    {
        switch (JmgParameters::find().TimecardInputMode)
        {
            case JmgTimecardInputMode::Timestamps:
                grid_regTime.skip(true);
                grid_regTime.allowEdit(false);
                grid_StartDateTime.skip(false);
                grid_StartDateTime.allowEdit(true);
                grid_StartTime.skip(false);
                grid_StartTime.allowEdit(true);
                grid_StopTime.skip(false);
                grid_StopTime.allowEdit(true);
                grid_StopDateTime.skip(false);
                grid_StopDateTime.allowEdit(true);
                break;
            case JmgTimecardInputMode::ElapsedTime:
            case JmgTimecardInputMode::NetTime:
                grid_regTime.skip(false);
                grid_regTime.allowEdit(true);
                grid_StartDateTime.skip(true);
                grid_StartDateTime.allowEdit(false);
                grid_StartTime.skip(true);
                grid_StartTime.allowEdit(false);
                grid_StopDateTime.skip(true);
                grid_StopDateTime.allowEdit(false);
                grid_StopTime.skip(true);
                grid_StopTime.allowEdit(false);
                break;
        }
    }
}

public void setRangeValues()
{
    workerRangeTable.value(queryValue(currentWorker));
    profileDateRangeTable.value(queryValue(currentProfileDate));

    jmgTimecardTable_DS.executeQuery();

    profileRangeProfileSpec.value(queryValue(jmgTimecardTable.ProfileId));
    dayIdRangeProfileSpec.value(queryValue(dayofwk(currentProfileDate) - 1));

    jmgProfileSpec_DS.executeQuery();
}

private void setShowStartDate()
{
    if (JmgParameters::find().TimecardShowStartDate == NoYes::No)
    {
        grid_StartDateTime.visible(false);
        grid_startTime.visible(true);
    }
    else
    {
        grid_StartDateTime.visible(true);
        grid_startTime.visible(false);
    }
}

private void setShowStopDate()
{
    if (JmgParameters::find().TimecardShowStopDate == NoYes::No)
    {
        grid_StopDateTime.visible(false);
        grid_stopTime.visible(true);
        //grid_StopDateTime.dateYear(1);
        //grid_StopDateTime.dateMonth(1);
        //grid_StopDateTime.dateDay(1);
    }
    else
    {
        grid_StopDateTime.visible(true);
        grid_stopTime.visible(false);
    }
}

private void synchronizeProfileDate()
{
    currentWeekNumber = wkofyr(currentProfileDate);
    this.workerProfileDateChanged();
    weekday.selection(dayofwk(currentProfileDate) - 1);
}

public void synchronizeWeekday(JmgDayId _dayId)
{
    if (dayofwk(currentProfileDate) - 1 != _dayId)
    {
        currentProfileDate = currentProfileDate + _dayId + 1 - dayofwk(currentProfileDate);
        this.workerProfileDateChanged();
    }
}

private void synchronizeWeekNumber(int _changeInWeeks)
{
    currentProfileDate = currentProfileDate + 7 * _changeInWeeks;
    weekday.selection(dayofwk(currentProfileDate) - 1);
    this.workerProfileDateChanged();
}

public void synchronizeWorker(JmgWorkerRecId _worker)
{
    currentWorker = _worker;
    element.design().caption(
        strfmt(
            "@SCM:EmployeeDataFormHeader",
            "@SYS39126",
            HcmWorker::find(currentWorker).PersonnelNumber,
            this.workerName()));
    this.workerProfileDateChanged();
}

public display DirPartyName workerName()
{
    return HcmWorker::worker2Name(currentWorker);
}

private void workerProfileDateChanged()
{
    JmgTimecardTable timecardTable;
    if (currentWorker && currentProfileDate)
    {
        timecardTable = JmgTimecardTable::find(currentWorker, currentProfileDate, true);

        if (!timecardTable)
        {
            timecardTable.initWorkerProfileDate(currentWorker, currentProfileDate);
            timecardTable.insert();
        }

        this.setRangeValues();
        this.recalculateProfileData();
    }
}

private JmgWeekNumberTmp getWeekNumbersTable()
{
    if (!weekNumberTmp || year(weekNumberTmp.ToDate) != year(currentProfileDate))
    {
        weekNumberTmp = JmgWeekNumberTmp::getWeekNumbersTable(year(currentProfileDate));
    }

    return weekNumberTmp;
}

