    this.cacheAddMethod(tablemethodstr(JmgTimecardTable,dateCalculated));
    this.cacheAddMethod(tablemethodstr(JmgTimecardTable,existsEventCode));
}

public int active()
{
    int ret;

    ret = super();

    if (jmgTimecardTable.dateLocked() == NoYes::Yes)
    {
         lockUnlock.text("@SYS25662");
    }
    else
    {
        lockUnlock.text("@SYS2675");
    }

    element.enableTimecardTrans(jmgTimecardTable_DS.cursor());

    element.setActiveButtons(jmgTimecardTable_DS.cursor());

    return ret;
}

public void modified()
{
    super();

    element.setRangeValues();
    element.recalculateProfileData();
}

