    super();
    this.cacheAddMethod(tablemethodstr(JmgTimecardTrans,jobDescription));

    if (JmgParameters::find().TimecardInputMode == JmgTimecardInputMode::Timestamps)
        this.cacheAddMethod(tablemethodstr(JmgTimecardTrans,regTime));
}

