    ret = super();

    element.setInputMode(jmgTimecardTrans);
    element.openClosePropertyFields(jmgTimecardTrans);

    return ret;
}

public void initValue()
{
    super();

    jmgTimecardTrans.Worker = currentWorker;
    jmgTimecardTrans.ProfileDate = currentProfileDate;
    jmgTimecardTrans.StartDateTime = DateTimeUtil::newDateTime(currentProfileDate,0,DateTimeUtil::getUserPreferredTimeZone());
    jmgTimecardTrans.StopDateTime = DateTimeUtil::newDateTime(currentProfileDate,timeMax(),DateTimeUtil::getUserPreferredTimeZone());
}

public void modified()
{
    super();
    jmgTimecardTrans.initValuesOnUpdate();
    element.openClosePropertyFields(jmgTimecardTrans);
}

public void lookup(FormControl _formControl, str _filterStr)
{
    ProjTableLookup projTableLookup;
    SysTableLookup  sysTableLookup;

    if (jmgTimecardTrans.JourRegType    == JmgJourRegTypeEnum::PrjActivity  ||
        jmgTimecardTrans.JourRegType    == JmgJourRegTypeEnum::PrjProject)
    {
        sysTableLookup  = SysTableLookup::newParameters(tablenum(ProjTable), _formControl);
        projTableLookup = new ProjTableLookup();
        sysTableLookup.addLookupfield(fieldnum(ProjTable, ProjId), true);
        sysTableLookup.addLookupfield(fieldnum(ProjTable, Name), false);
        sysTableLookup.parmQuery(projTableLookup.buildProjTableQueryResource(
            jmgTimecardTrans.TableId, jmgTimecardTrans.ProjCategory, ResourceFacade::findOrCreateByWorker(jmgTimecardTrans.Worker)));
        sysTableLookup.performFormLookup();
    }
    else
    {
        super(_formControl, _filterStr);
    }
}

public boolean validate()
{
    boolean isSwitchCode = false;

    if (jmgTimecardTrans.JourRegType == JmgJourRegTypeEnum::SwitchCode)
    {
        error("@SYS4008457");
        isSwitchCode = true;
    }

    return super() && !isSwitchCode;
}

