public class Kanban extends FormRun
{
    KanbanFormHelper        kanbanFormHelper;
    FormButtonManager       formButtonManager;
    KanbanCard              kanbanCard;
    Kanban                  callerKanban;
    ItemId                  lastSelectedItemId;

}

public void init()
{
    callerKanban = this.callerKanban();

    super();

    // When open from KanbanRules form, not only select provided Kanban, but also show only kanbans related to that Kanban Rule.
    if (   element.args().caller()
        && element.args().caller().name() == formstr(KanbanRules)
        && callerKanban)
    {
        KanbanRule_DS.queryBuildDataSource().addRange(fieldnum(KanbanRule, RecId)).value(queryValue(callerKanban.KanbanRule));
    }

    // Creates and initializes the KanbanFormHelper and FormButtonManager objects
    kanbanFormHelper = KanbanFormHelper::newForm(this, inventDimPegging_ds);
    kanbanFormHelper.setDatasources(kanban, kanbanjob, inventDimPegging);
    formButtonManager = FormButtonManager::construct();

    this.registerButtonRules();

    kanbanFormHelper.updateDesign(InventDimFormDesignUpdate::Init);

    this.updateDesign();
}

