/// <summary>
///    Refreshes the form data.
/// </summary>
public void doRefresh()
{
    Kanban_DS.research(true);
    KanbanJob_DS.research();

    this.updateDesign();
}

private void registerButtonRules()
{
    formButtonManager.parmRuleImplementer(kanbanFormHelper);

    formButtonManager.registerButtonRule(btnGrpNew, btnCreateKanbans, methodStr(KanbanFormHelper, ruleCheckKanbanDuplicate),  ButtonRuleEffectType::Enabled);
    formButtonManager.registerButtonRule(btnGrpActions, btnCancel, methodStr(KanbanFormHelper, ruleCheckKanbanCancel),  ButtonRuleEffectType::Enabled);
    formButtonManager.registerButtonRule(btnGrpActions, btnEmptyReset, methodStr(KanbanFormHelper, ruleCheckKanbanEmptyReset),  ButtonRuleEffectType::Enabled);
    formButtonManager.registerButtonRule(btnGrpActions, btnCreateEvent, methodStr(KanbanFormHelper, ruleCheckKanbanCreateEvent),  ButtonRuleEffectType::Enabled);
    formButtonManager.registerButtonRule(btnGrpActions, btnEmpty, methodStr(KanbanFormHelper, ruleCheckKanbanEmpty),  ButtonRuleEffectType::Enabled);
    formButtonManager.registerButtonRule(btnGrpView, btnViewSourceRequirement, methodStr(KanbanFormHelper, ruleCheckViewSourceRequirement),  ButtonRuleEffectType::Enabled);
    formButtonManager.registerButtonRule(btnGrpJobs, btnCancelJobs, methodStr(KanbanFormHelper, ruleCheckKanbanJobCancel),  ButtonRuleEffectType::Enabled);
    formButtonManager.registerButtonRule(btnGrpAssign, btnAssignBOMVersion, methodStr(KanbanFormHelper, ruleCheckBOMVersionAssign),  ButtonRuleEffectType::Enabled);
    formButtonManager.registerButtonRule(btnGrpAssign, btnReassignRule, methodStr(KanbanFormHelper, ruleCheckRuleReassign),  ButtonRuleEffectType::Enabled);
}

private Kanban callerKanban()
{
    if (   element.args()
        && element.args().record())
    {
        callerKanban = KanbanFormHelper::kanbanFromRecord(element.args().record());
    }

    if (   !callerKanban
        && element.args()
        && element.args().lookupTable() == tableNum(Kanban)
        && element.args().lookupField() == fieldNum(Kanban,KanbanId)
        && element.args().lookupValue())
    {
        callerKanban = Kanban::findKanbanId(element.args().lookupValue());
    }

    return callerKanban;
}

private InventDimCtrl_Frm inventDimSetupObject()
{
    return kanbanFormHelper.parmInventDimFormSetupPegging();
}

private void updateDesign()
{
    kanbanCard = KanbanCard::findKanban(kanban.RecId);

    kanbanCard_CardId.text(kanbanCard.CardId);
    kanbanCard_CardId.allowEdit(   kanban.Status == LeanKanbanStatus::NotAssigned
                                && (kanban.Manual == NoYes::No
                                ||  kanban.kanbanRule().type().manualKanbansNeedCards()));

    kanbanCard_PrintStatus.selection(kanbanCard.RecId ? kanbanCard.PrintStatus : 0);
    kanbanCard_PrintStatus.enabled(kanbanCard.RecId);

    kanbanFormHelper.updateDesign(InventDimFormDesignUpdate::Active);

    formButtonManager.refresh(true);

    this.design().caption(strFmt("@SYS191501", "@SYS131576", kanban.KanbanId));
}

/// <summary>
/// Refreshes the product information fact box if needed.
/// </summary>
/// <remarks>
/// Dynalink will not refresh when we change between lines with the same item id, so we force a refresh.
/// </remarks>
private void refreshProductInformationFactBox()
{
    if (Kanban.ItemId && lastSelectedItemId == Kanban.ItemId)
    {
        FormPartReferenceControl partRefControl = element.getPartControlByName("EcoResProductInformationFactBox");
        partRefControl.refresh();
    }
    lastSelectedItemId = Kanban.ItemId;
}

public void init()
{
    super();

    QueryBuildDataSource qbds = this.queryBuildDataSource();

    qbds.addRange(fieldNum(Kanban, LegalEntity)).value(queryValue(CompanyInfo::current()));

    qbds.sortClear();
    qbds.addSortField(fieldnum(Kanban, ItemId));
    qbds.addSortField(fieldnum(Kanban, Status));
    qbds.addSortField(fieldnum(Kanban, DueDateTime));
    qbds.addSortField(fieldnum(Kanban, QuantityOrdered));

    QueryBuildRange statusRange = qbds.addRange(fieldnum(Kanban, Status));
    if (!callerKanban || callerKanban.Status != LeanKanbanStatus::Emptied)
    {
        statusRange.value(SysQuery::valueNot(LeanKanbanStatus::Emptied));
    }

    qbds.addRange(fieldnum(Kanban, ItemId));
}

public void write()
{
    KanbanRule      rule;

    super();

    ttsbegin;

    if (kanbanCard.RecId)
    {
        kanbanCard.setPrintStatus(kanbanCard_PrintStatus.selection(),true);
    }

    // Card updated?
    if (kanbanCard.CardId != kanban.KanbanCardId)
    {
        if (kanbanCard.CardId != '')
        {
            // Get the card reassigned to the latest active rule
            rule = KanbanRule::find(kanbanCard.KanbanRule);
            rule.status().reassignCard(kanbanCard);

            KanbanCard::assignToKanban(kanbanCard, kanban);
        }
        else
        {
            KanbanCard::unAssign(kanban);
        }
    }

    ttscommit;
}

public int active()
{
    int ret;

    ret = super();

    element.updateDesign();
    element.refreshProductInformationFactBox();

    return ret;
}

