public display PurchStatus purchStatus(KanbanJob _kanbanJob)
{
    PurchLine               purchLine;
    KanbanJobPurchaseLine   kanbanJobPurchaseLine = KanbanJobPurchaseLine::findKanbanJob(_kanbanJob.RecId);

    if (kanbanJobPurchaseLine)
    {
        purchLine = PurchLine::findInventTransId(kanbanJobPurchaseLine.PurchLineInventTransId);
    }

    return purchLine.PurchStatus;
}

