public display ProductReceiptId productReceiptId(KanbanJob _kanbanJob)
{
    VendPackingSlipTrans        vendPackingSlipTrans;
    KanbanJobReceiptAdviceLine  kanbanJobReceiptAdviceLine = KanbanJobReceiptAdviceLine::findKanbanJob(_kanbanJob.RecId);

    if (kanbanJobReceiptAdviceLine)
    {
        vendPackingSlipTrans = VendPackingSlipTrans::findRecId(kanbanJobReceiptAdviceLine.ReceiptAdviceLine);
    }

    return vendPackingSlipTrans.PackingSlipId;
}

