public void init()
{
    super();

    QueryBuildDataSource qbds = this.queryBuildDataSource();

    qbds.sortClear();

    InventDimensionEnumerator productDimensions = InventProductDimension::getEnumeratorForEnabledDimensions();
    while (productDimensions.moveNext())
    {
        FieldId productDimFieldId = productDimensions.currentInventDimFieldId();

        qbds.addSortField(productDimFieldId);
        qbds.addRange(productDimFieldId);
    }
}

public void executeQuery()
{
    Query                   query;
    QueryBuildDataSource    qbdsKanbanJobTmpPegging;
    QueryBuildDataSource    qbdsInvenDim;

    delete_from kanbanJobTmpPegging;

    kanbanJobTmpPegging.init([
                             [
                             kanbanJob.RecId
                             ,
                             kanban.RecId]
                             ]
                             ,
                             true
                             );

    // Create the query to link to Inventory dimensions
    query = new Query();

    qbdsKanbanJobTmpPegging = query.addDataSource(tableNum(KanbanJobTmpPegging));
    qbdsKanbanJobTmpPegging.name(kanbanJobTmpPegging_ds.name());

    qbdsInvenDim = qbdsKanbanJobTmpPegging.addDataSource(tableNum(InventDim));
    qbdsInvenDim.name(inventDimPegging_ds.name());
    qbdsInvenDim.addLink(fieldNum(KanbanJobTmpPegging, InventDimId), fieldNum(InventDim, InventDimId));
    qbdsInvenDim.joinMode(JoinMode::InnerJoin);

    kanbanJobTmpPegging_ds.query(query);

    inventDimPegging_ds.executeQuery();

    super();
}

