public void clicked()
{
    LeanRuleReassignmentWizard  leanRuleReassignmentWizard;
    Args                        args    = new Args();
    Set                         kanbans = new Set(Types::Int64);

    // Override menu item call (menu item set to enforce security)
    kanbans.add(kanban.RecId);
    args.object(kanbans);
    leanRuleReassignmentWizard = LeanRuleReassignmentWizard::newStandard(args);
    leanRuleReassignmentWizard.runMain();

    if (leanRuleReassignmentWizard.firstResult())
    {
        callerKanban = Kanban::find(leanRuleReassignmentWizard.currentResultReassignedKanbanRecId());

        kanban_ds.positionToRecord(callerKanban);
        element.doRefresh();
    }
}

