public void lookup()
{
    Query               freeCardsQuery;
    Args                args;
    FormRun             formRun;

    freeCardsQuery = kanbanRule.status().getCardsQuery(true, false);

    args = new Args();
    args.name(formStr(KanbanFreeCardLookup));
    args.caller(this);
    args.parmObject(freeCardsQuery);
    formRun = classfactory.formRunClass(args);
    formRun.init();
    this.performFormLookup(formRun);
}

public boolean modified()
{
    boolean         ret;
    KanbanCardId    kanbanCardId;

    ret = super();

    kanbanCardId = kanbanCard_CardId.text();
    if (kanbanCardId != kanbanCard.CardId)
    {
        kanbanCard = KanbanCard::findCardId(kanbanCardId);
        kanbanCard_PrintStatus.enabled(kanbanCard.RecId);
        kanbanCard_PrintStatus.selection(kanbanCard.RecId ? kanbanCard.PrintStatus : 0);
    }

    return ret;
}

public boolean validate()
{
    boolean         ret;
    KanbanCardId    kanbanCardId;

    ret = super();

    kanbanCardId = kanbanCard_CardId.text();
    if (kanbanCardId != kanbanCard.CardId)
    {
        kanbanCard = KanbanCard::findCardId(kanbanCardId);
        if (   !kanbanCard
                    && kanbanCardId != '')
        {
            // The specified card does not exist
            ret = checkFailed("@SYS135797");
        }
    }

    return ret;
}

public boolean modified()
{
    boolean result = super();

    kanbanCard.setPrintStatus(kanbanCard_PrintStatus.selection(),true);

    return result;
}

