    str                                     dimensionDisplayButtonClicked;

    boolean                                 refreshAllowed;
    boolean                                 resetSelection;
    boolean                                 refreshKanbanJobTmpPegging;
    boolean                                 formInitialized;

    KanbanScanMode                          registrationMode;

    RefRecId                                updatedPlanActivityService;

    boolean                                 testMode;
    boolean                                 closeNow;

    //default settings for job list, refresh cycle and test mode
    #define.DefaultVisibleCompletedJobs(3)
    #define.DefaultVisibleInProgressJobs(250)
    #define.DefaultVisibleNotPlannedJobs(250)
    #define.DefaultRefreshCycle(0)
