public void close()
{
    super();

    kanbanBoardTmpFilterCriteriaCustom = kanbanBoardTmpFilterCriteria;

    xSysLastValue::saveLast(element);
}

public void doResearch()
{
    element.refreshContent();
}

public void init()
{
    formInitialized = false;

    //values are needed by the time datasources are initialized (as part of the call to super)
    xSysLastValue::getLast(element);

    if (element.args() &&
        element.args().dataset() == tablenum(KanbanBoardTmpFilterCriteria))
    {
        kanbanBoardTmpFilterCriteriaCustom = element.args().record();
    }

    super();

    kanbanBoardTransferJobForm = KanbanBoardTransferJobForm::newForm(this,inventDimTransferJob_ds,inventDimPickListPegging_ds);

    if (element.args())
    {
        testMode = element.args().parmEnum() == NoYes::Yes;
    }

    //pick filter criteria when the form opens
    if (!testMode)
    {
        this.initFilter();
    }

    if (!testMode)
    {
        this.initDesign();
    }

    //update the overall design (load data, etc)
    this.updateDesign(InventDimFormDesignUpdate::Init);
}

private void initActionPane()
{
    //set up button managers
    formButtonManager        = FormButtonManager::construct();
    kanbanBoardButtonManager = KanbanBoardTransferJobButtonManager::newDefault(kanbanBoardTransferJobList);

    formButtonManager.parmRuleImplementer(kanbanBoardButtonManager);
    formButtonManager.setDatasources(kanbanBoardTmpTransferJob);

    //register the buttons/groups of the transfer action pane
    formButtonManager.registerButtonRule(transferButtonTopGroup,startTransferButton,methodStr(KanbanBoardTransferJobButtonManager,checkStartTransfer));
    formButtonManager.registerButtonRule(transferButtonTopGroup,endTransferButton,methodStr(KanbanBoardTransferJobButtonManager,checkCompleteTransfer));

    formButtonManager.registerButtonRule(transferButtonGroup,updatePickingListButton,methodStr(KanbanBoardTransferJobButtonManager,checkUpdatePickingList));
    formButtonManager.registerButtonRule(transferButtonGroup,updateRegistrationButton,methodStr(KanbanBoardTransferJobButtonManager,checkUpdateRegistration));

    formButtonManager.registerButtonRule(ScanModeButtonGroup,registrationModeStartButton,methodStr(KanbanBoardTransferJobButtonManager,checkRegistrationMode));
    formButtonManager.registerButtonRule(ScanModeButtonGroup,registrationModeCompleteButton,methodStr(KanbanBoardTransferJobButtonManager,checkRegistrationMode));
    formButtonManager.registerButtonRule(ScanModeButtonGroup,registrationModeEmptyButton,methodStr(KanbanBoardTransferJobButtonManager,checkRegistrationMode));
    formButtonManager.registerButtonRule(ScanModeButtonGroup,registrationModeSelectButton,methodStr(KanbanBoardTransferJobButtonManager,checkRegistrationMode));

    formButtonManager.registerButtonRule(peggingButtonGroup,createEventButton,methodStr(KanbanBoardTransferJobButtonManager,checkCreateKanbans));

    formButtonManager.registerButtonRule(maintainStatusButtonGroup,revertStatusButton,methodStr(KanbanBoardTransferJobButtonManager,checkRevertStatus));
    formButtonManager.registerButtonRule(maintainStatusButtonGroup,cancelButton,methodStr(KanbanBoardTransferJobButtonManager,checkCancel));
    formButtonManager.registerButtonRule(maintainStatusButtonGroup,prioritizeButton,methodStr(KanbanBoardTransferJobButtonManager,checkPrioritize));

    formButtonManager.registerButtonRule(printButtonGroup,printPickingPickListButton,methodStr(KanbanBoardTransferJobButtonManager,checkPrintPickingList));
    formButtonManager.registerButtonRule(printButtonGroup,printKanbanCardButton,methodStr(KanbanBoardTransferJobButtonManager,checkPrintKanbanCard));
    formButtonManager.registerButtonRule(printButtonGroup,printBillOfLadingButton,methodStr(KanbanBoardTransferJobButtonManager,checkPrintBillOfLading));

