private void initFilter(boolean _forceDialog = false)
{
    kanbanBoardTmpFilterCriteriaCustom.setTmpData(kanbanBoardTmpFilterCriteria);
    kanbanBoardTmpFilterCriteria.data(kanbanBoardTmpFilterCriteriaCustom);

    kanbanBoardTmpFilterCriteria.KanbanCardId = '';

    if (KanbanBoardTransferJobForm::validateFilter(kanbanBoardTmpFilterCriteria) == false)
    {
        kanbanBoardTmpFilterCriteria.clear();
    }

    kanbanBoardTmpFilterCriteria.SuppressKanbanQtyOverview = true;

    kanbanBoardTmpFilterCriteria.insert();

    this.setFormTitleCaption();

    KanbanBoardForm::showConfigServiceProgress();

    this.refreshContent();
}

