public InventDimCtrl_Frm inventDimSetupObject()
{
    InventDimCtrl_Frm   inventDimCtrl_Frm;

    switch (this.selectedControl().name())
    {
        case dimensionDisplayTransferJobListButton.name():
            inventDimCtrl_Frm = kanbanBoardTransferJobForm.inventDimFormSetupTransferJobList();
            break;
        case dimensionDisplayPickAndEventListButton.name():
            inventDimCtrl_Frm = kanbanBoardTransferJobForm.inventDimFormSetupPickListPegging();
            break;
        default:
            throw error(Error::wrongUseOfFunction(funcName()));
    }

    return inventDimCtrl_Frm;
}

