public KanbanScanMode parmRegistrationMode(KanbanScanMode _registrationMode = registrationMode)
{
    registrationMode    = _registrationMode;

    if (!prmisDefault(_registrationMode))
    {
        refreshAllowed                            = registrationMode != KanbanScanMode::SelectKanban;

        if (refreshAllowed && kanbanBoardTmpFilterCriteria.KanbanCardId)
        {
            kanbanBoardTmpFilterCriteria.KanbanCardId = '';
            element.refreshContent();
        }

        this.updateRegistrationModeButtons();
    }

    return registrationMode;
}

