public void refreshContent(boolean _forceRefresh = false)
{
    if (!formInitialized)
    {
        return;
    }

    if (refreshAllowed || _forceRefresh)
    {
        this.refreshKanbanJobList();
        this.setButtonAccess();
    }

    this.refreshMessageBoard();

    this.startRefreshCycleTimer();
}

public void refreshKanbanJobDetails()
{
    if (PickingListTab.isActivePage())
    {
        kanbanJobTmpPickList_ds.executeQuery();
    }

    if (PeggingTab.isActivePage())
    {
        kanbanJobTmpPegging_ds.executeQuery();
    }
}

public void refreshKanbanJobList()
{
    //hide from/to specifics if already defined by filter
    transferJobGrid_IssueInventLocationId.visible(!kanbanBoardTmpFilterCriteria.InventLocationIdFrom);
    transferJobGrid_IssueWMSLocationId.visible(!kanbanBoardTmpFilterCriteria.wmsLocationIdFrom);
    transferJobGrid_ReceiptInventLocationId.visible(!kanbanBoardTmpFilterCriteria.InventLocationIdTo);
    transferJobGrid_ReceiptWMSLocationId.visible(!kanbanBoardTmpFilterCriteria.wmsLocationIdTo);

    kanbanBoardTmpTransferJob_ds.research();
}

public void refreshKanbanJobTmpPegging(boolean _rebuild = true, boolean _forceRefresh = false)
{
    if (PeggingTab.isActivePage() || _forceRefresh)
    {
        refreshKanbanJobTmpPegging = !_rebuild;
        kanbanJobTmpPegging_ds.executeQuery();
        refreshKanbanJobTmpPegging = false;
    }
}

